'use client'

import React, { useMemo } from 'react'

// Utility functions for generating ARIA labels
export const ariaLabels = {
  // Dashboard card labels
  card: {
    interactive: (title: string) => `${title} card, interactive`,
    loading: (title?: string) => `${title ? `${title} card` : 'Card'} loading`,
    error: (title?: string, error?: string) => 
      `${title ? `${title} card` : 'Card'} error${error ? `: ${error}` : ''}`,
    metric: (title: string, value: string | number, unit?: string, trend?: string) => {
      let label = `${title}: ${value}`
      if (unit) label += ` ${unit}`
      if (trend) label += `, trend ${trend}`
      return label
    }
  },

  // Chart labels
  chart: {
    container: (type: string, title?: string) => 
      `${type} chart${title ? ` showing ${title}` : ''}`,
    interactive: (type: string) => 
      `Interactive ${type} chart. Use arrow keys to navigate, Enter to select`,
    loading: (type: string) => `${type} chart loading`,
    error: (type: string, error?: string) => 
      `${type} chart error${error ? `: ${error}` : ''}`,
    noData: (type: string) => `${type} chart with no data available`,
    element: (type: string, label: string, value: string | number, index: number, total: number) =>
      `${type} element ${index + 1} of ${total}: ${label}, value ${value}`,
    legend: (label: string, active: boolean) => 
      `Legend item: ${label}, ${active ? 'active' : 'inactive'}`,
    axis: (type: 'x' | 'y', label?: string) => 
      `${type.toUpperCase()} axis${label ? `: ${label}` : ''}`
  },

  // Navigation labels
  navigation: {
    main: 'Main navigation',
    breadcrumb: 'Breadcrumb navigation',
    pagination: 'Pagination navigation',
    skip: (target: string) => `Skip to ${target}`,
    toggle: (expanded: boolean, target: string) => 
      `${expanded ? 'Collapse' : 'Expand'} ${target}`,
    current: (page: string) => `Current page: ${page}`
  },

  // Form labels
  form: {
    required: (label: string) => `${label}, required`,
    optional: (label: string) => `${label}, optional`,
    invalid: (label: string, error: string) => `${label}, invalid: ${error}`,
    help: (label: string, help: string) => `${label}. ${help}`,
    search: (placeholder?: string) => 
      `Search${placeholder ? ` ${placeholder}` : ''}`,
    filter: (type: string, value?: string) => 
      `Filter by ${type}${value ? `, current value: ${value}` : ''}`
  },

  // Table labels
  table: {
    container: (caption?: string, rowCount?: number) => {
      let label = 'Data table'
      if (caption) label += `: ${caption}`
      if (rowCount !== undefined) label += `, ${rowCount} rows`
      return label
    },
    sortable: (column: string, direction?: 'asc' | 'desc') => {
      let label = `Sort by ${column}`
      if (direction) {
        label += `, currently sorted ${direction === 'asc' ? 'ascending' : 'descending'}`
      }
      return label
    },
    row: (index: number, total: number, selected?: boolean) => {
      let label = `Row ${index + 1} of ${total}`
      if (selected) label += ', selected'
      return label
    },
    cell: (content: string, column: string, row: number) => 
      `${column}: ${content}, row ${row + 1}`
  },

  // Status and feedback labels
  status: {
    loading: (content?: string) => `Loading${content ? ` ${content}` : ''}`,
    success: (message: string) => `Success: ${message}`,
    error: (message: string) => `Error: ${message}`,
    warning: (message: string) => `Warning: ${message}`,
    info: (message: string) => `Information: ${message}`,
    progress: (current: number, total: number, label?: string) => 
      `${label ? `${label}: ` : ''}${current} of ${total} complete`
  },

  // Modal and dialog labels
  modal: {
    dialog: (title: string) => `${title} dialog`,
    close: (title?: string) => `Close${title ? ` ${title}` : ''} dialog`,
    confirm: (action: string) => `Confirm ${action}`,
    cancel: 'Cancel'
  },

  // Filter and search labels
  filter: {
    applied: (count: number) => `${count} filter${count !== 1 ? 's' : ''} applied`,
    clear: 'Clear all filters',
    results: (count: number, total?: number) => {
      if (total !== undefined) {
        return `Showing ${count} of ${total} results`
      }
      return `${count} result${count !== 1 ? 's' : ''} found`
    }
  }
}

// Hook for generating dynamic ARIA labels
export function useAriaLabels() {
  return useMemo(() => ariaLabels, [])
}

// Component for providing ARIA descriptions
interface AriaDescriptionProps {
  id: string
  children: string
  className?: string
}

export function AriaDescription({ id, children, className }: AriaDescriptionProps) {
  return (
    <div
      id={id}
      className={`sr-only ${className || ''}`}
      role="text"
    >
      {children}
    </div>
  )
}

// Component for live regions
interface LiveRegionProps {
  children: React.ReactNode
  priority?: 'polite' | 'assertive'
  atomic?: boolean
  relevant?: 'additions' | 'removals' | 'text' | 'all'
  className?: string
}

export function LiveRegion({ 
  children, 
  priority = 'polite', 
  atomic = true,
  relevant = 'all',
  className 
}: LiveRegionProps) {
  return (
    <div
      className={`sr-only ${className || ''}`}
      aria-live={priority}
      aria-atomic={atomic}
      aria-relevant={relevant}
      role={priority === 'assertive' ? 'alert' : 'status'}
    >
      {children}
    </div>
  )
}

// Component for providing accessible names and descriptions
interface AccessibleElementProps {
  children: React.ReactElement
  label?: string
  description?: string
  labelledBy?: string
  describedBy?: string
  role?: string
  expanded?: boolean
  selected?: boolean
  disabled?: boolean
  required?: boolean
  invalid?: boolean
  current?: boolean | 'page' | 'step' | 'location' | 'date' | 'time'
}

export function AccessibleElement({
  children,
  label,
  description,
  labelledBy,
  describedBy,
  role,
  expanded,
  selected,
  disabled,
  required,
  invalid,
  current
}: AccessibleElementProps) {
  const descriptionId = description ? `${(children.props as any).id || 'element'}-description` : undefined

  return (
    <>
      {React.cloneElement(children, {
        ...(children.props as any),
        'aria-label': label || (children.props as any)['aria-label'],
        'aria-labelledby': labelledBy || (children.props as any)['aria-labelledby'],
        'aria-describedby': describedBy || descriptionId || (children.props as any)['aria-describedby'],
        'role': role || (children.props as any).role,
        'aria-expanded': expanded !== undefined ? expanded : (children.props as any)['aria-expanded'],
        'aria-selected': selected !== undefined ? selected : (children.props as any)['aria-selected'],
        'aria-disabled': disabled !== undefined ? disabled : (children.props as any)['aria-disabled'],
        'aria-required': required !== undefined ? required : (children.props as any)['aria-required'],
        'aria-invalid': invalid !== undefined ? invalid : (children.props as any)['aria-invalid'],
        'aria-current': current !== undefined ? current : (children.props as any)['aria-current']
      })}
      {description && descriptionId && (
        <AriaDescription id={descriptionId}>
          {description}
        </AriaDescription>
      )}
    </>
  )
}

// Utility for generating chart element ARIA labels
export function generateChartAriaLabel(
  element: any,
  index: number,
  total: number,
  chartType: string
): string {
  const { label, value, x, y, data } = element

  let ariaLabel = `${chartType} element ${index + 1} of ${total}`

  if (label) {
    ariaLabel += `: ${label}`
  }

  if (value !== undefined) {
    ariaLabel += `, value ${value}`
  } else if (y !== undefined) {
    ariaLabel += `, y value ${y}`
    if (x !== undefined) {
      ariaLabel += `, x value ${x}`
    }
  } else if (data !== undefined) {
    ariaLabel += `, data ${data}`
  }

  return ariaLabel
}

// Utility for generating table cell ARIA labels
export function generateTableCellAriaLabel(
  content: string,
  columnHeader: string,
  rowIndex: number,
  columnIndex: number
): string {
  return `${columnHeader}: ${content}, row ${rowIndex + 1}, column ${columnIndex + 1}`
}

// Utility for generating form field ARIA labels
export function generateFormFieldAriaLabel(
  label: string,
  required?: boolean,
  invalid?: boolean,
  errorMessage?: string,
  helpText?: string
): string {
  let ariaLabel = label

  if (required) {
    ariaLabel += ', required'
  }

  if (invalid && errorMessage) {
    ariaLabel += `, invalid: ${errorMessage}`
  }

  if (helpText) {
    ariaLabel += `. ${helpText}`
  }

  return ariaLabel
}

// Utility for generating navigation ARIA labels
export function generateNavigationAriaLabel(
  currentPage: string,
  totalPages?: number,
  pageIndex?: number
): string {
  let ariaLabel = `Current page: ${currentPage}`

  if (totalPages && pageIndex !== undefined) {
    ariaLabel += `, page ${pageIndex + 1} of ${totalPages}`
  }

  return ariaLabel
}

'use client'

import { useEffect, useRef, useCallback } from 'react'
import { cn } from '@/lib/utils'

interface AnnouncementProps {
  message: string
  priority: 'polite' | 'assertive'
  delay?: number
}

interface ScreenReaderAnnouncerProps {
  className?: string
}

// Global announcement queue to manage multiple announcements
const announcementQueue: AnnouncementProps[] = []
let isProcessing = false

export function ScreenReaderAnnouncer({ className }: ScreenReaderAnnouncerProps) {
  const politeRef = useRef<HTMLDivElement>(null)
  const assertiveRef = useRef<HTMLDivElement>(null)
  const timeoutRef = useRef<NodeJS.Timeout>()

  // Process the announcement queue
  const processQueue = useCallback(() => {
    if (isProcessing || announcementQueue.length === 0) return

    isProcessing = true
    const announcement = announcementQueue.shift()!

    const targetRef = announcement.priority === 'assertive' ? assertiveRef : politeRef

    if (targetRef.current) {
      // Clear previous content
      targetRef.current.textContent = ''

      // Add new announcement with optional delay
      const delay = announcement.delay || 0
      timeoutRef.current = setTimeout(() => {
        if (targetRef.current) {
          targetRef.current.textContent = announcement.message

          // Clear the message after a short delay to allow for re-announcements
          setTimeout(() => {
            if (targetRef.current) {
              targetRef.current.textContent = ''
            }
            isProcessing = false
            processQueue() // Process next announcement
          }, 100)
        }
      }, delay)
    } else {
      isProcessing = false
      processQueue()
    }
  }, [])

  // Set up global announcement function
  useEffect(() => {
    // Make announce function globally available
    const announceFunction = (
      message: string, 
      priority: 'polite' | 'assertive' = 'polite',
      delay?: number
    ) => {
      announcementQueue.push({ message, priority, delay })
      processQueue()
    }

    ;(window as any).announceToScreenReader = announceFunction

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
      }
      // Clean up global function
      delete (window as any).announceToScreenReader
    }
  }, [processQueue])

  return (
    <div className={cn('screen-reader-announcer', className)}>
      {/* Polite announcements - won't interrupt current speech */}
      <div
        ref={politeRef}
        className="sr-only"
        aria-live="polite"
        aria-atomic="true"
        role="status"
      />

      {/* Assertive announcements - will interrupt current speech */}
      <div
        ref={assertiveRef}
        className="sr-only"
        aria-live="assertive"
        aria-atomic="true"
        role="alert"
      />
    </div>
  )
}

// Hook for using screen reader announcements
export function useScreenReaderAnnouncer() {
  const announce = useCallback((
    message: string, 
    priority: 'polite' | 'assertive' = 'polite',
    delay?: number
  ) => {
    if (typeof window !== 'undefined' && (window as any).announceToScreenReader) {
      ;(window as any).announceToScreenReader(message, priority, delay)
    }
  }, [])

  const announcePolite = useCallback((message: string, delay?: number) => {
    announce(message, 'polite', delay)
  }, [announce])

  const announceAssertive = useCallback((message: string, delay?: number) => {
    announce(message, 'assertive', delay)
  }, [announce])

  // Announce data updates
  const announceDataUpdate = useCallback((
    dataType: string, 
    updateType: 'loaded' | 'updated' | 'error' | 'filtered' = 'updated',
    details?: string
  ) => {
    let message = `${dataType} ${updateType}`
    if (details) {
      message += `: ${details}`
    }
    announce(message, 'polite')
  }, [announce])

  // Announce chart interactions
  const announceChartInteraction = useCallback((
    action: string,
    element: string,
    value?: string | number,
    context?: string
  ) => {
    let message = `${action} ${element}`
    if (value !== undefined) {
      message += ` with value ${value}`
    }
    if (context) {
      message += ` in ${context}`
    }
    announce(message, 'polite')
  }, [announce])

  // Announce navigation changes
  const announceNavigation = useCallback((
    destination: string,
    context?: string
  ) => {
    let message = `Navigated to ${destination}`
    if (context) {
      message += ` ${context}`
    }
    announce(message, 'polite')
  }, [announce])

  // Announce filter changes
  const announceFilterChange = useCallback((
    filterType: string,
    filterValue: string,
    resultCount?: number
  ) => {
    let message = `Filter applied: ${filterType} set to ${filterValue}`
    if (resultCount !== undefined) {
      message += `. Showing ${resultCount} results`
    }
    announce(message, 'polite')
  }, [announce])

  // Announce loading states
  const announceLoading = useCallback((
    content: string,
    isLoading: boolean
  ) => {
    const message = isLoading 
      ? `Loading ${content}` 
      : `${content} loaded`
    announce(message, 'polite')
  }, [announce])

  // Announce errors
  const announceError = useCallback((
    error: string,
    context?: string
  ) => {
    let message = `Error: ${error}`
    if (context) {
      message += ` in ${context}`
    }
    announce(message, 'assertive')
  }, [announce])

  return {
    announce,
    announcePolite,
    announceAssertive,
    announceDataUpdate,
    announceChartInteraction,
    announceNavigation,
    announceFilterChange,
    announceLoading,
    announceError
  }
}

// Component for announcing data changes
interface DataAnnouncerProps {
  data: any
  dataType: string
  loading?: boolean
  error?: string
  children: React.ReactNode
}

export function DataAnnouncer({ 
  data, 
  dataType, 
  loading = false, 
  error, 
  children 
}: DataAnnouncerProps) {
  const { announceDataUpdate, announceLoading, announceError } = useScreenReaderAnnouncer()
  const previousDataRef = useRef(data)
  const previousLoadingRef = useRef(loading)

  useEffect(() => {
    // Announce loading state changes
    if (loading !== previousLoadingRef.current) {
      announceLoading(dataType, loading)
      previousLoadingRef.current = loading
    }

    // Announce data updates
    if (!loading && data !== previousDataRef.current) {
      if (data && !previousDataRef.current) {
        announceDataUpdate(dataType, 'loaded')
      } else if (data) {
        announceDataUpdate(dataType, 'updated')
      }
      previousDataRef.current = data
    }

    // Announce errors
    if (error) {
      announceError(error, dataType)
    }
  }, [data, dataType, loading, error, announceDataUpdate, announceLoading, announceError])

  return <>{children}</>
}

// Component for announcing chart interactions
interface ChartAnnouncerProps {
  chartType: string
  data: any
  selectedElement?: any
  children: React.ReactNode
}

export function ChartAnnouncer({ 
  chartType, 
  data, 
  selectedElement, 
  children 
}: ChartAnnouncerProps) {
  const { announceChartInteraction } = useScreenReaderAnnouncer()
  const previousSelectedRef = useRef(selectedElement)

  useEffect(() => {
    if (selectedElement && selectedElement !== previousSelectedRef.current) {
      const elementType = selectedElement.type || 'element'
      const value = selectedElement.value || selectedElement.y || selectedElement.data
      announceChartInteraction('Selected', elementType, value, `${chartType} chart`)
      previousSelectedRef.current = selectedElement
    }
  }, [selectedElement, chartType, announceChartInteraction])

  return <>{children}</>
}

// Component for announcing filter changes
interface FilterAnnouncerProps {
  filters: Record<string, any>
  resultCount?: number
  children: React.ReactNode
}

export function FilterAnnouncer({ 
  filters, 
  resultCount, 
  children 
}: FilterAnnouncerProps) {
  const { announceFilterChange } = useScreenReaderAnnouncer()
  const previousFiltersRef = useRef(filters)

  useEffect(() => {
    const currentFilters = JSON.stringify(filters)
    const previousFilters = JSON.stringify(previousFiltersRef.current)

    if (currentFilters !== previousFilters) {
      // Find which filter changed
      Object.keys(filters).forEach(key => {
        if (filters[key] !== previousFiltersRef.current?.[key]) {
          announceFilterChange(key, String(filters[key]), resultCount)
        }
      })
      previousFiltersRef.current = filters
    }
  }, [filters, resultCount, announceFilterChange])

  return <>{children}</>
}

import React from 'react'
import { render, screen, fireEvent, waitFor } from '@testing-library/react'
import userEvent from '@testing-library/user-event'
import { axe, toHaveNoViolations } from 'jest-axe'
import { ChartCard } from '../chart-card'
import { BarChart3 } from 'lucide-react'

expect.extend(toHaveNoViolations)

// Mock ChartWrapper component
jest.mock('../../charts/chart-wrapper', () => ({
  ChartWrapper: ({ type, data, onDataClick, className }: any) => (
    <div 
      className={className}
      data-testid="chart-wrapper"
      data-chart-type={type}
      onClick={() => onDataClick?.({ label: 'test-data', x: 1, y: 100 })}
    >
      Mock Chart: {type}
      <div>Data points: {data?.datasets?.[0]?.data?.length || 0}</div>
    </div>
  )
}))

describe('ChartCard', () => {
  const user = userEvent.setup()

  const mockChartData = {
    datasets: [
      {
        label: 'Test Dataset',
        data: [
          { x: 1, y: 100, label: 'Point 1' },
          { x: 2, y: 200, label: 'Point 2' },
          { x: 3, y: 150, label: 'Point 3' }
        ]
      }
    ],
    labels: ['Jan', 'Feb', 'Mar']
  }

  const mockChartConfig = {
    colors: {
      primary: '#3F51B5',
      secondary: '#B19ADA',
      accent: '#FF6B35',
      success: '#10B981',
      warning: '#F59E0B',
      error: '#EF4444'
    },
    legend: {
      show: true,
      position: 'bottom' as const
    }
  }

  describe('Basic Rendering', () => {
    it('renders with title and chart', async () => {
      render(
        <ChartCard
          title="Test Chart"
          chartType="bar"
          data={mockChartData}
        />
      )

      expect(screen.getByText('Test Chart')).toBeInTheDocument()

      await waitFor(() => {
        expect(screen.getByTestId('chart-wrapper')).toBeInTheDocument()
        expect(screen.getByText('Mock Chart: bar')).toBeInTheDocument()
      })
    })

    it('renders with subtitle and icon', async () => {
      render(
        <ChartCard
          title="Chart with Icon"
          subtitle="Test subtitle"
          icon={BarChart3}
          chartType="line"
          data={mockChartData}
        />
      )

      expect(screen.getByText('Chart with Icon')).toBeInTheDocument()
      expect(screen.getByText('Test subtitle')).toBeInTheDocument()

      const icon = document.querySelector('[aria-hidden="true"]')
      expect(icon).toBeInTheDocument()
    })

    it('passes chart configuration correctly', async () => {
      render(
        <ChartCard
          title="Configured Chart"
          chartType="pie"
          data={mockChartData}
          chartConfig={mockChartConfig}
        />
      )

      await waitFor(() => {
        expect(screen.getByTestId('chart-wrapper')).toBeInTheDocument()
        expect(screen.getByText('Mock Chart: pie')).toBeInTheDocument()
      })
    })
  })

  describe('Chart Actions', () => {
    it('renders filter button when filterable', () => {
      render(
        <ChartCard
          title="Filterable Chart"
          chartType="bar"
          data={mockChartData}
          filterable
        />
      )

      const filterButton = screen.getByLabelText('Filter chart data')
      expect(filterButton).toBeInTheDocument()
    })

    it('renders export dropdown when exportable', async () => {
      render(
        <ChartCard
          title="Exportable Chart"
          chartType="bar"
          data={mockChartData}
          exportable
        />
      )

      const exportButton = screen.getByLabelText('Export chart')
      expect(exportButton).toBeInTheDocument()

      await user.click(exportButton)

      expect(screen.getByText('Export as PNG')).toBeInTheDocument()
      expect(screen.getByText('Export as SVG')).toBeInTheDocument()
      expect(screen.getByText('Export as PDF')).toBeInTheDocument()
    })

    it('handles filter button click', async () => {
      const mockOnFilter = jest.fn()

      render(
        <ChartCard
          title="Filterable Chart"
          chartType="bar"
          data={mockChartData}
          filterable
          onFilter={mockOnFilter}
        />
      )

      const filterButton = screen.getByLabelText('Filter chart data')
      await user.click(filterButton)

      expect(mockOnFilter).toHaveBeenCalledWith({
        type: 'string',
        value: 'chart-filter',
        operator: 'equals'
      })
    })

    it('handles export menu clicks', async () => {
      const consoleSpy = jest.spyOn(console, 'log').mockImplementation()

      render(
        <ChartCard
          title="Exportable Chart"
          chartType="bar"
          data={mockChartData}
          exportable
        />
      )

      const exportButton = screen.getByLabelText('Export chart')
      await user.click(exportButton)

      const pngOption = screen.getByText('Export as PNG')
      await user.click(pngOption)

      expect(consoleSpy).toHaveBeenCalledWith('Exporting chart as png')

      consoleSpy.mockRestore()
    })

    it('renders custom actions alongside chart actions', () => {
      render(
        <ChartCard
          title="Chart with Actions"
          chartType="bar"
          data={mockChartData}
          filterable
          exportable
          actions={<button>Custom Action</button>}
        />
      )

      expect(screen.getByLabelText('Filter chart data')).toBeInTheDocument()
      expect(screen.getByLabelText('Export chart')).toBeInTheDocument()
      expect(screen.getByRole('button', { name: 'Custom Action' })).toBeInTheDocument()
    })
  })

  describe('Loading and Error States', () => {
    it('renders loading skeleton', () => {
      render(
        <ChartCard
          title="Loading Chart"
          chartType="bar"
          data={mockChartData}
          loading
        />
      )

      // Should show skeleton elements
      const skeletons = document.querySelectorAll('.animate-pulse')
      expect(skeletons.length).toBeGreaterThan(0)

      // Should not show chart
      expect(screen.queryByTestId('chart-wrapper')).not.toBeInTheDocument()
    })

    it('renders error state', () => {
      render(
        <ChartCard
          title="Error Chart"
          chartType="bar"
          data={mockChartData}
          error="Failed to load chart data"
        />
      )

      expect(screen.getByText('Unable to load chart')).toBeInTheDocument()
      expect(screen.getByText('Failed to load chart data')).toBeInTheDocument()

      // Should not show chart
      expect(screen.queryByTestId('chart-wrapper')).not.toBeInTheDocument()
    })

    it('shows loading spinner while chart component loads', () => {
      render(
        <ChartCard
          title="Loading Chart Component"
          chartType="bar"
          data={mockChartData}
        />
      )

      // Chart should be rendered (mocked)
      expect(screen.getByText('Loading Chart Component')).toBeInTheDocument()
    })
  })

  describe('Chart Interaction', () => {
    it('handles chart data clicks for filtering', async () => {
      const mockOnFilter = jest.fn()

      render(
        <ChartCard
          title="Interactive Chart"
          chartType="bar"
          data={mockChartData}
          interactive
          onFilter={mockOnFilter}
        />
      )

      await waitFor(() => {
        const chart = screen.getByTestId('chart-wrapper')
        fireEvent.click(chart)
      })

      expect(mockOnFilter).toHaveBeenCalledWith({
        type: 'string',
        value: 'test-data',
        operator: 'equals'
      })
    })

    it('does not handle clicks when not interactive', async () => {
      const mockOnFilter = jest.fn()

      render(
        <ChartCard
          title="Non-interactive Chart"
          chartType="bar"
          data={mockChartData}
          onFilter={mockOnFilter}
        />
      )

      await waitFor(() => {
        const chart = screen.getByTestId('chart-wrapper')
        fireEvent.click(chart)
      })

      // onFilter should still be called because chart wrapper handles it
      expect(mockOnFilter).toHaveBeenCalled()
    })
  })

  describe('Chart Types', () => {
    const chartTypes = ['line', 'bar', 'pie', 'heatmap', 'area', 'scatter'] as const

    chartTypes.forEach(chartType => {
      it(`renders ${chartType} chart correctly`, async () => {
        render(
          <ChartCard
            title={`${chartType} Chart`}
            chartType={chartType}
            data={mockChartData}
          />
        )

        await waitFor(() => {
          expect(screen.getByTestId('chart-wrapper')).toHaveAttribute('data-chart-type', chartType)
          expect(screen.getByText(`Mock Chart: ${chartType}`)).toBeInTheDocument()
        })
      })
    })
  })

  describe('Custom Content', () => {
    it('renders additional children content', async () => {
      render(
        <ChartCard
          title="Chart with Content"
          chartType="bar"
          data={mockChartData}
        >
          <div>Additional chart content</div>
        </ChartCard>
      )

      expect(screen.getByText('Additional chart content')).toBeInTheDocument()
    })
  })

  describe('Accessibility', () => {
    it('meets WCAG accessibility standards', async () => {
      const { container } = render(
        <ChartCard
          title="Accessible Chart"
          subtitle="Chart subtitle"
          icon={BarChart3}
          chartType="bar"
          data={mockChartData}
          filterable
          exportable
        />
      )

      const results = await axe(container)
      expect(results).toHaveNoViolations()
    })

    it('provides proper ARIA labels for action buttons', () => {
      render(
        <ChartCard
          title="Chart with Actions"
          chartType="bar"
          data={mockChartData}
          filterable
          exportable
        />
      )

      expect(screen.getByLabelText('Filter chart data')).toBeInTheDocument()
      expect(screen.getByLabelText('Export chart')).toBeInTheDocument()
    })
  })

  describe('Responsive Behavior', () => {
    it('handles empty data gracefully', async () => {
      const emptyData = {
        datasets: [],
        labels: []
      }

      render(
        <ChartCard
          title="Empty Chart"
          chartType="bar"
          data={emptyData}
        />
      )

      await waitFor(() => {
        expect(screen.getByTestId('chart-wrapper')).toBeInTheDocument()
        expect(screen.getByText('Data points: 0')).toBeInTheDocument()
      })
    })

    it('applies custom className', () => {
      render(
        <ChartCard
          title="Custom Chart"
          chartType="bar"
          data={mockChartData}
          className="custom-chart-class"
        />
      )

      const card = document.querySelector('.chart-card')
      expect(card).toHaveClass('custom-chart-class')
    })
  })
})

import React from 'react'
import { render, screen, fireEvent, waitFor } from '@testing-library/react'
import userEvent from '@testing-library/user-event'
import { axe, toHaveNoViolations } from 'jest-axe'
import { DashboardCard } from '../dashboard-card'
import { Activity, Users } from 'lucide-react'

expect.extend(toHaveNoViolations)

describe('DashboardCard', () => {
  const user = userEvent.setup()

  describe('Basic Rendering', () => {
    it('renders with title and subtitle', () => {
      render(
        <DashboardCard
          title="Test Card"
          subtitle="Test subtitle"
        />
      )

      expect(screen.getByText('Test Card')).toBeInTheDocument()
      expect(screen.getByText('Test subtitle')).toBeInTheDocument()
    })

    it('renders with icon', () => {
      render(
        <DashboardCard
          title="Test Card"
          icon={Activity}
        />
      )

      expect(screen.getByText('Test Card')).toBeInTheDocument()
      // Icon should be present but hidden from screen readers
      const icon = document.querySelector('[aria-hidden="true"]')
      expect(icon).toBeInTheDocument()
    })

    it('renders with metric data', () => {
      render(
        <DashboardCard
          title="Test Card"
          metric={{
            value: 1234,
            unit: 'users',
            trend: 'up',
            trendValue: '+12%'
          }}
        />
      )

      expect(screen.getByText('1234')).toBeInTheDocument()
      expect(screen.getByText('users')).toBeInTheDocument()
      expect(screen.getByText('+12%')).toBeInTheDocument()
      expect(screen.getByLabelText('Trend: up, +12%')).toBeInTheDocument()
    })

    it('renders with children content', () => {
      render(
        <DashboardCard title="Test Card">
          <div>Custom content</div>
        </DashboardCard>
      )

      expect(screen.getByText('Custom content')).toBeInTheDocument()
    })

    it('renders with actions', () => {
      render(
        <DashboardCard
          title="Test Card"
          actions={<button>Action</button>}
        />
      )

      expect(screen.getByRole('button', { name: 'Action' })).toBeInTheDocument()
    })
  })

  describe('Size Variants', () => {
    it('applies correct size classes', () => {
      const { rerender } = render(<DashboardCard size="sm" title="Small Card" />)
      expect(document.querySelector('.dashboard-card-sm')).toBeInTheDocument()

      rerender(<DashboardCard size="md" title="Medium Card" />)
      expect(document.querySelector('.dashboard-card-md')).toBeInTheDocument()

      rerender(<DashboardCard size="lg" title="Large Card" />)
      expect(document.querySelector('.dashboard-card-lg')).toBeInTheDocument()

      rerender(<DashboardCard size="xl" title="Extra Large Card" />)
      expect(document.querySelector('.dashboard-card-xl')).toBeInTheDocument()
    })
  })

  describe('Priority Variants', () => {
    it('applies correct priority classes', () => {
      const { rerender } = render(<DashboardCard priority="low" title="Low Priority" />)
      expect(document.querySelector('.dashboard-card-priority-low')).toBeInTheDocument()

      rerender(<DashboardCard priority="medium" title="Medium Priority" />)
      expect(document.querySelector('.dashboard-card-priority-medium')).toBeInTheDocument()

      rerender(<DashboardCard priority="high" title="High Priority" />)
      expect(document.querySelector('.dashboard-card-priority-high')).toBeInTheDocument()

      rerender(<DashboardCard priority="critical" title="Critical Priority" />)
      expect(document.querySelector('.dashboard-card-priority-critical')).toBeInTheDocument()
    })
  })

  describe('Loading State', () => {
    it('renders loading skeleton', () => {
      render(<DashboardCard loading title="Loading Card" />)

      expect(screen.getByRole('status')).toBeInTheDocument()
      expect(screen.getByLabelText('Loading card content')).toBeInTheDocument()

      // Should show skeleton elements
      const skeletons = document.querySelectorAll('.animate-pulse')
      expect(skeletons.length).toBeGreaterThan(0)
    })

    it('does not render content when loading', () => {
      render(
        <DashboardCard
          loading
          title="Loading Card"
          metric={{ value: 100, unit: 'items' }}
        >
          <div>Content</div>
        </DashboardCard>
      )

      expect(screen.queryByText('Loading Card')).not.toBeInTheDocument()
      expect(screen.queryByText('100')).not.toBeInTheDocument()
      expect(screen.queryByText('Content')).not.toBeInTheDocument()
    })
  })

  describe('Error State', () => {
    it('renders error message', () => {
      render(<DashboardCard error="Something went wrong" />)

      expect(screen.getByRole('alert')).toBeInTheDocument()
      expect(screen.getByLabelText('Card error')).toBeInTheDocument()
      expect(screen.getByText('Error')).toBeInTheDocument()
      expect(screen.getByText('Something went wrong')).toBeInTheDocument()
    })

    it('does not render content when error', () => {
      render(
        <DashboardCard
          error="Error occurred"
          title="Error Card"
          metric={{ value: 100, unit: 'items' }}
        >
          <div>Content</div>
        </DashboardCard>
      )

      expect(screen.queryByText('Error Card')).not.toBeInTheDocument()
      expect(screen.queryByText('100')).not.toBeInTheDocument()
      expect(screen.queryByText('Content')).not.toBeInTheDocument()
    })
  })

  describe('Interactive Features', () => {
    it('handles click events when interactive', async () => {
      const handleClick = jest.fn()
      render(
        <DashboardCard
          title="Interactive Card"
          interactive
          onClick={handleClick}
        />
      )

      const card = screen.getByRole('button')
      expect(card).toBeInTheDocument()
      expect(card).toHaveAttribute('aria-label', 'Interactive Card card')

      await user.click(card)
      expect(handleClick).toHaveBeenCalledTimes(1)
    })

    it('handles keyboard navigation when interactive', async () => {
      const handleClick = jest.fn()
      render(
        <DashboardCard
          title="Interactive Card"
          interactive
          onClick={handleClick}
        />
      )

      const card = screen.getByRole('button')

      // Test Enter key
      card.focus()
      await user.keyboard('{Enter}')
      expect(handleClick).toHaveBeenCalledTimes(1)

      // Test Space key
      await user.keyboard(' ')
      expect(handleClick).toHaveBeenCalledTimes(2)
    })

    it('does not handle clicks when not interactive', async () => {
      const handleClick = jest.fn()
      render(
        <DashboardCard
          title="Non-interactive Card"
          onClick={handleClick}
        />
      )

      const card = document.querySelector('.dashboard-card')
      expect(card).not.toHaveAttribute('role', 'button')
      expect(card).not.toHaveAttribute('tabindex')

      if (card) {
        await user.click(card)
      }
      expect(handleClick).not.toHaveBeenCalled()
    })

    it('applies interactive styles when interactive', () => {
      render(
        <DashboardCard
          title="Interactive Card"
          interactive
          onClick={() => {}}
        />
      )

      const card = screen.getByRole('button')
      expect(card).toHaveClass('cursor-pointer')
    })
  })

  describe('Trend Indicators', () => {
    it('renders up trend with correct styling', () => {
      render(
        <DashboardCard
          metric={{
            value: 100,
            trend: 'up',
            trendValue: '+5%'
          }}
        />
      )

      const trendElement = screen.getByLabelText('Trend: up, +5%')
      expect(trendElement).toHaveClass('bg-green-100', 'text-green-800')
    })

    it('renders down trend with correct styling', () => {
      render(
        <DashboardCard
          metric={{
            value: 100,
            trend: 'down',
            trendValue: '-3%'
          }}
        />
      )

      const trendElement = screen.getByLabelText('Trend: down, -3%')
      expect(trendElement).toHaveClass('bg-red-100', 'text-red-800')
    })

    it('renders neutral trend with correct styling', () => {
      render(
        <DashboardCard
          metric={{
            value: 100,
            trend: 'neutral',
            trendValue: '0%'
          }}
        />
      )

      const trendElement = screen.getByLabelText('Trend: neutral, 0%')
      expect(trendElement).toHaveClass('bg-gray-100', 'text-gray-800')
    })
  })

  describe('Custom Styling', () => {
    it('applies custom className', () => {
      render(
        <DashboardCard
          title="Custom Card"
          className="custom-class"
        />
      )

      const card = document.querySelector('.dashboard-card')
      expect(card).toHaveClass('custom-class')
    })

    it('combines default and custom classes correctly', () => {
      render(
        <DashboardCard
          title="Custom Card"
          size="lg"
          priority="high"
          className="custom-class"
        />
      )

      const card = document.querySelector('.dashboard-card')
      expect(card).toHaveClass(
        'dashboard-card',
        'dashboard-card-lg',
        'dashboard-card-priority-high',
        'custom-class'
      )
    })
  })

  describe('Accessibility', () => {
    it('meets WCAG accessibility standards', async () => {
      const { container } = render(
        <DashboardCard
          title="Accessible Card"
          subtitle="Card subtitle"
          icon={Users}
          metric={{
            value: 1234,
            unit: 'users',
            trend: 'up',
            trendValue: '+12%'
          }}
          interactive
          onClick={() => {}}
        >
          <div>Card content</div>
        </DashboardCard>
      )

      const results = await axe(container)
      expect(results).toHaveNoViolations()
    })

    it('provides proper ARIA labels for interactive cards', () => {
      render(
        <DashboardCard
          title="Interactive Card"
          interactive
          onClick={() => {}}
        />
      )

      const card = screen.getByRole('button')
      expect(card).toHaveAttribute('aria-label', 'Interactive Card card')
    })

    it('provides proper ARIA labels for loading state', () => {
      render(<DashboardCard loading />)

      expect(screen.getByRole('status')).toBeInTheDocument()
      expect(screen.getByLabelText('Loading card content')).toBeInTheDocument()
    })

    it('provides proper ARIA labels for error state', () => {
      render(<DashboardCard error="Test error" />)

      expect(screen.getByRole('alert')).toBeInTheDocument()
      expect(screen.getByLabelText('Card error')).toBeInTheDocument()
    })

    it('hides decorative icons from screen readers', () => {
      render(
        <DashboardCard
          title="Card with Icon"
          icon={Activity}
        />
      )

      const icon = document.querySelector('[aria-hidden="true"]')
      expect(icon).toBeInTheDocument()
    })
  })

  describe('Responsive Behavior', () => {
    it('handles long titles gracefully', () => {
      render(
        <DashboardCard
          title="This is a very long title that should be handled gracefully by the component"
          subtitle="This is also a long subtitle that should truncate properly"
        />
      )

      const title = screen.getByText(/This is a very long title/)
      const subtitle = screen.getByText(/This is also a long subtitle/)

      expect(title).toBeInTheDocument()
      expect(subtitle).toBeInTheDocument()
      expect(subtitle).toHaveClass('truncate')
    })

    it('handles metric values with different types', () => {
      const { rerender } = render(
        <DashboardCard
          metric={{ value: 1234 }}
        />
      )
      expect(screen.getByText('1234')).toBeInTheDocument()

      rerender(
        <DashboardCard
          metric={{ value: '1.2K' }}
        />
      )
      expect(screen.getByText('1.2K')).toBeInTheDocument()

      rerender(
        <DashboardCard
          metric={{ value: 0 }}
        />
      )
      expect(screen.getByText('0')).toBeInTheDocument()
    })
  })

  describe('Performance', () => {
    it('renders efficiently with minimal re-renders', () => {
      const renderSpy = jest.fn()

      function TestCard(props: any) {
        renderSpy()
        return <DashboardCard {...props} />
      }

      const { rerender } = render(
        <TestCard title="Test Card" />
      )

      expect(renderSpy).toHaveBeenCalledTimes(1)

      // Re-render with same props should not cause additional renders
      rerender(<TestCard title="Test Card" />)
      expect(renderSpy).toHaveBeenCalledTimes(2)
    })
  })
})

import React from 'react'
import { render, screen, fireEvent, waitFor } from '@testing-library/react'
import userEvent from '@testing-library/user-event'
import { axe, toHaveNoViolations } from 'jest-axe'
import { DataTableCard } from '../data-table-card'
import { Table } from 'lucide-react'

expect.extend(toHaveNoViolations)

// Mock URL.createObjectURL and URL.revokeObjectURL for export tests
global.URL.createObjectURL = jest.fn(() => 'mock-url')
global.URL.revokeObjectURL = jest.fn()

// Mock document.createElement for export tests
const mockClick = jest.fn()
const mockAnchor = {
  href: '',
  download: '',
  click: mockClick
}

// Store original createElement
const originalCreateElement = document.createElement

jest.spyOn(document, 'createElement').mockImplementation((tagName) => {
  if (tagName === 'a') {
    return mockAnchor as any
  }
  return originalCreateElement.call(document, tagName)
})

describe('DataTableCard', () => {
  const user = userEvent.setup()

  const mockColumns = [
    { key: 'id', label: 'ID', sortable: true },
    { key: 'name', label: 'Name', sortable: true, searchable: true },
    { key: 'email', label: 'Email', sortable: true, searchable: true },
    { key: 'status', label: 'Status', sortable: false },
    { 
      key: 'actions', 
      label: 'Actions', 
      sortable: false,
      render: (value: any, row: any) => (
        <button data-testid={`action-${row.id}`}>Edit</button>
      )
    }
  ]

  const mockData = [
    { id: 1, name: 'John Doe', email: 'john@example.com', status: 'Active' },
    { id: 2, name: 'Jane Smith', email: 'jane@example.com', status: 'Inactive' },
    { id: 3, name: 'Bob Johnson', email: 'bob@example.com', status: 'Active' },
    { id: 4, name: 'Alice Brown', email: 'alice@example.com', status: 'Pending' },
    { id: 5, name: 'Charlie Wilson', email: 'charlie@example.com', status: 'Active' }
  ]

  beforeEach(() => {
    jest.clearAllMocks()
  })

  describe('Basic Rendering', () => {
    it('renders with title and table data', () => {
      render(
        <DataTableCard
          title="User Table"
          columns={mockColumns}
          data={mockData}
        />
      )

      expect(screen.getByText('User Table')).toBeInTheDocument()
