import React, { Suspense, useCallback } from 'react'
import { cn } from '@/lib/utils'
import { DashboardCard } from './dashboard-card'
import { FilterableChart } from '../filters/filterable-chart'
import { ChartWrapper } from '../charts/chart-wrapper'
import { useCrossCardFiltering } from '../filters/filter-synchronizer'
import { Download, Filter, MoreHorizontal } from 'lucide-react'
import { Button } from '@/components/ui/button'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import { Skeleton } from '@/components/ui/skeleton'
import type { ChartCardProps, ChartDataPoint, LegendItem, FilterValue } from '@/types/dashboard'

interface EnhancedChartCardProps extends ChartCardProps {
  // Enhanced filtering props
  enableCrossCardFiltering?: boolean
  filterMappings?: {
    dataClick?: (dataPoint: ChartDataPoint) => Record<string, FilterValue>
    legendClick?: (legendItem: LegendItem) => Record<string, FilterValue>
  }
  // Export props
  exportFilename?: string
  onExportStart?: (format: string) => void
  onExportComplete?: (format: string) => void
  onExportError?: (error: Error, format: string) => void
}

export function ChartCard({
  title,
  subtitle,
  icon,
  chartType,
  data,
  chartConfig,
  exportable = false,
  filterable = false,
  enableCrossCardFiltering = true,
  filterMappings,
  exportFilename,
  onExportStart,
  onExportComplete,
  onExportError,
  children,
  actions,
  loading = false,
  error,
  className,
  size = 'md',
  priority = 'medium',
  interactive = true,
  onClick,
  onFilter,
}: EnhancedChartCardProps) {
  // Handle chart data clicks for filtering
  const handleDataClick = useCallback((dataPoint: ChartDataPoint) => {
    if (onFilter) {
      // Create a filter based on the clicked data point
      const filterValue: FilterValue = {
        type: 'string',
        value: dataPoint.label || dataPoint.x?.toString() || '',
        operator: 'equals'
      }
      onFilter(filterValue)
    }
  }, [onFilter])

  // Handle legend clicks for filtering
  const handleLegendClick = useCallback((legendItem: LegendItem) => {
    if (onFilter) {
      // Create a filter based on the clicked legend item
      const filterValue: FilterValue = {
        type: 'string',
        value: legendItem.label,
        operator: 'equals'
      }
      onFilter(filterValue)
    }
  }, [onFilter])

  const handleExport = (format: 'png' | 'svg' | 'pdf') => {
    onExportStart?.(format)
    // TODO: Implement chart export functionality
    console.log(`Exporting chart as ${format}`)
    onExportComplete?.(format)
  }

  const handleFilter = () => {
    if (onFilter) {
      // Create a generic filter for the chart
      onFilter({
        type: 'string',
        value: title || 'chart',
        operator: 'equals'
      })
    }
  }

  const renderChartActions = () => {
    if (!exportable && !filterable && !actions) return null

    const chartActions = (
      <div className="flex items-center space-x-2">
        {filterable && (
          <Button
            variant="ghost"
            size="sm"
            onClick={handleFilter}
            className="h-8 w-8 p-0"
            aria-label="Filter chart data"
          >
            <Filter className="h-4 w-4" />
          </Button>
        )}

        {exportable && (
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                className="h-8 w-8 p-0"
                aria-label="Export chart"
              >
                <Download className="h-4 w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem onClick={() => handleExport('png')}>
                Export as PNG
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => handleExport('svg')}>
                Export as SVG
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => handleExport('pdf')}>
                Export as PDF
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        )}

        {actions && (
          <>
            {(exportable || filterable) && (
              <div className="w-px h-4 bg-border" />
            )}
            {actions}
          </>
        )}
      </div>
    )

    return chartActions
  }

  const renderChart = () => {
    if (loading) {
      return (
        <div className="space-y-3">
          <Skeleton className="h-4 w-full" />
          <Skeleton className="h-32 w-full" />
          <div className="flex justify-center space-x-4">
            <Skeleton className="h-3 w-16" />
            <Skeleton className="h-3 w-16" />
            <Skeleton className="h-3 w-16" />
          </div>
        </div>
      )
    }

    if (error) {
      return (
        <div className="flex items-center justify-center h-32 text-muted-foreground">
          <div className="text-center">
            <p className="text-sm font-medium">Unable to load chart</p>
            <p className="text-xs mt-1">{error}</p>
          </div>
        </div>
      )
    }

    return (
      <Suspense
        fallback={
          <div className="flex items-center justify-center h-32">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
          </div>
        }
      >
        {enableCrossCardFiltering ? (
          <FilterableChart
            type={chartType}
            data={data}
            config={chartConfig}
            className="chart-card-wrapper"
            interactive={interactive}
            exportable={exportable}
            exportFilename={exportFilename || title || 'chart'}
            enableFiltering={filterable}
            filterMappings={filterMappings}
            onDataClick={handleDataClick}
            onLegendClick={handleLegendClick}
            onExportStart={onExportStart}
            onExportComplete={onExportComplete}
            onExportError={onExportError}
          />
        ) : (
          <ChartWrapper
            type={chartType}
            data={data}
            config={chartConfig}
            className="chart-card-wrapper"
            interactive={interactive}
            exportable={exportable}
            exportFilename={exportFilename || title || 'chart'}
            onDataClick={handleDataClick}
            onLegendClick={handleLegendClick}
            onExportStart={onExportStart}
            onExportComplete={onExportComplete}
            onExportError={onExportError}
          />
        )}
      </Suspense>
    )
  }

  return (
    <DashboardCard
      title={title}
      subtitle={subtitle}
      icon={icon}
      actions={renderChartActions()}
      loading={false} // Handle loading internally
      error={undefined} // Handle error internally
      className={cn('chart-card', className)}
      size={size}
      priority={priority}
      interactive={false} // Chart handles its own interactivity
      onClick={onClick}
      onFilter={onFilter}
    >
      <div className="chart-card-content">
        {renderChart()}
        {children}
      </div>
    </DashboardCard>
  )
}

import React, { useCallback } from 'react'
import { cn } from '@/lib/utils'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Skeleton } from '@/components/ui/skeleton'
import { AlertCircle, TrendingUp, TrendingDown, Minus } from 'lucide-react'
import { useAriaLabels, AriaDescription } from '../accessibility/aria-labels'
import { useScreenReaderAnnouncer } from '../accessibility/screen-reader-announcer'
import { TouchInteraction, useIsTouchDevice, useIsMobile } from '../mobile/touch-interactions'
import { MobileTooltip } from '../mobile/mobile-tooltip'
import type { DashboardCardProps, FilterValue } from '@/types/dashboard'

export function DashboardCard({
  title,
  subtitle,
  icon: Icon,
  metric,
  children,
  actions,
  loading = false,
  error,
  className,
  size = 'md',
  priority = 'medium',
  interactive = false,
  onClick,
  onFilter,
}: DashboardCardProps) {
  const ariaLabels = useAriaLabels()
  const { announceError } = useScreenReaderAnnouncer()
  const isTouchDevice = useIsTouchDevice()
  const isMobile = useIsMobile()

  // Generate unique IDs for ARIA relationships
  const cardId = React.useId()
  const titleId = `${cardId}-title`
  const subtitleId = `${cardId}-subtitle`
  const metricId = `${cardId}-metric`
  const errorId = `${cardId}-error`
  const descriptionId = `${cardId}-description`
  const cardSizeClasses = {
    sm: 'dashboard-card-sm',
    md: 'dashboard-card-md',
    lg: 'dashboard-card-lg',
    xl: 'dashboard-card-xl',
  }

  const priorityClasses = {
    low: 'dashboard-card-priority-low',
    medium: 'dashboard-card-priority-medium',
    high: 'dashboard-card-priority-high',
    critical: 'dashboard-card-priority-critical',
  }

  const getTrendIcon = (trend: 'up' | 'down' | 'neutral') => {
    switch (trend) {
      case 'up':
        return <TrendingUp className="h-3 w-3" />
      case 'down':
        return <TrendingDown className="h-3 w-3" />
      case 'neutral':
        return <Minus className="h-3 w-3" />
      default:
        return null
    }
  }

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (interactive && (event.key === 'Enter' || event.key === ' ')) {
      event.preventDefault()
      onClick?.()
    }
  }

  // Handle touch interactions
  const handleTouchTap = useCallback((event: TouchEvent) => {
    if (interactive && onClick) {
      onClick()
    }
  }, [interactive, onClick])

  const handleTouchLongPress = useCallback((event: TouchEvent) => {
    if (interactive && onFilter) {
      // Long press could trigger filter options
      onFilter({ type: 'string', value: title || 'card' })
    }
  }, [interactive, onFilter, title])

  // Generate comprehensive ARIA label
  const getAriaLabel = () => {
    if (loading) {
      return ariaLabels.card.loading(title)
    }

    if (error) {
      return ariaLabels.card.error(title, error)
    }

    if (interactive && title) {
      return ariaLabels.card.interactive(title)
    }

    if (metric && title) {
      return ariaLabels.card.metric(
        title, 
        metric.value, 
        metric.unit, 
        metric.trend
      )
    }

    return title || 'Dashboard card'
  }

  // Announce errors to screen readers
  React.useEffect(() => {
    if (error) {
      announceError(error, title ? `${title} card` : 'Dashboard card')
    }
  }, [error, title, announceError])

  if (loading) {
    return (
      <Card 
        className={cn('dashboard-card', cardSizeClasses[size], className)}
        role="status"
        aria-label={getAriaLabel()}
        aria-describedby={descriptionId}
      >
        <AriaDescription id={descriptionId}>
          {title ? `${title} card is loading` : 'Card content is loading'}
        </AriaDescription>
        <CardHeader className="dashboard-card-header">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Skeleton className="h-5 w-5 rounded" />
              <div className="space-y-2">
                <Skeleton className="h-4 w-24" />
                <Skeleton className="h-3 w-16" />
              </div>
            </div>
            <Skeleton className="h-8 w-8 rounded" />
          </div>
        </CardHeader>
        <CardContent className="dashboard-card-content">
          <div className="space-y-3">
            <div className="flex items-baseline space-x-2">
              <Skeleton className="h-8 w-16" />
              <Skeleton className="h-4 w-8" />
              <Skeleton className="h-6 w-12 rounded-full" />
            </div>
            <Skeleton className="h-20 w-full rounded" />
          </div>
        </CardContent>
      </Card>
    )
  }

  if (error) {
    return (
      <Card 
        className={cn(
          'dashboard-card border-red-200 bg-red-50 dark:bg-red-950/20', 
          cardSizeClasses[size], 
          className
        )}
        role="alert"
        aria-label={getAriaLabel()}
        aria-describedby={errorId}
      >
        <AriaDescription id={errorId}>
          {title ? `${title} card has an error: ${error}` : `Card error: ${error}`}
        </AriaDescription>
        <CardContent className="dashboard-card-content">
          <div className="flex items-center space-x-2 text-red-600 dark:text-red-400">
            <AlertCircle className="h-4 w-4 flex-shrink-0" />
            <span className="text-sm font-medium">Error</span>
          </div>
          <p className="text-sm text-red-600 dark:text-red-400 mt-2">{error}</p>
        </CardContent>
      </Card>
    )
  }

  const cardContent = (
    <Card
      className={cn(
        'dashboard-card',
        cardSizeClasses[size],
        priorityClasses[priority],
        interactive && [
          'cursor-pointer hover:shadow-md focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2',
          'transition-all duration-200 ease-out',
          // Adjust hover effects for touch devices
          !isTouchDevice && 'hover:scale-[1.02]',
          // Touch-specific styles
          isTouchDevice && 'active:scale-[0.98] active:shadow-lg',
        ],
        // Mobile-specific optimizations
        isMobile && [
          'mb-4', // Add bottom margin for mobile stacking
          'mx-2', // Add horizontal margin for mobile
        ],
        className
      )}
      onClick={interactive && !isTouchDevice ? onClick : undefined}
      onKeyDown={interactive ? handleKeyDown : undefined}
      tabIndex={interactive ? 0 : undefined}
      role={interactive ? 'button' : 'region'}
      aria-label={getAriaLabel()}
      aria-labelledby={title ? titleId : undefined}
      aria-describedby={subtitle ? subtitleId : descriptionId}
    >
      <AriaDescription id={descriptionId}>
        {interactive 
          ? 'Interactive dashboard card. Press Enter or Space to activate.'
          : 'Dashboard card with information display.'
        }
      </AriaDescription>
      {(title || subtitle || Icon || actions) && (
        <CardHeader className="dashboard-card-header">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              {Icon && (
                <Icon 
                  className="h-5 w-5 text-muted-foreground flex-shrink-0" 
                  aria-hidden="true"
                />
              )}
              <div className="min-w-0 flex-1">
                {title && (
                  <CardTitle 
                    id={titleId}
                    className="dashboard-card-title text-base font-semibold leading-tight"
                  >
                    {title}
                  </CardTitle>
                )}
                {subtitle && (
                  <p 
                    id={subtitleId}
                    className="dashboard-card-subtitle text-sm text-muted-foreground mt-1 truncate"
                  >
                    {subtitle}
                  </p>
                )}
              </div>
            </div>
            {actions && (
              <div className="dashboard-card-actions flex-shrink-0">
                {actions}
              </div>
            )}
          </div>
        </CardHeader>
      )}

      <CardContent className="dashboard-card-content">
        {metric && (
          <div 
            id={metricId}
            className="dashboard-card-metric mb-4"
            role="group"
            aria-label={ariaLabels.card.metric(
              title || 'Metric', 
              metric.value, 
              metric.unit, 
              metric.trend
            )}
          >
            <div className="flex items-baseline space-x-2 flex-wrap">
              <span 
                className="text-2xl font-bold text-foreground"
                aria-label={`Value: ${metric.value}${metric.unit ? ` ${metric.unit}` : ''}`}
              >
                {metric.value}
              </span>
              {metric.unit && (
                <span 
                  className="text-sm text-muted-foreground"
                  aria-label={`Unit: ${metric.unit}`}
                >
                  {metric.unit}
                </span>
              )}
              {metric.trend && metric.trendValue && (
                <span
                  className={cn(
                    'inline-flex items-center gap-1 text-xs px-2 py-1 rounded-full font-medium',
                    metric.trend === 'up' && 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400',
                    metric.trend === 'down' && 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400',
                    metric.trend === 'neutral' && 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300'
                  )}
                  role="img"
                  aria-label={`Trend: ${metric.trend === 'up' ? 'increasing' : metric.trend === 'down' ? 'decreasing' : 'stable'} by ${metric.trendValue}`}
                >
                  {getTrendIcon(metric.trend)}
                  <span aria-hidden="true">{metric.trendValue}</span>
                </span>
              )}
            </div>
          </div>
        )}
        {children}
      </CardContent>
    </Card>
  )

  // Wrap with touch interactions for touch devices
  if (interactive && isTouchDevice) {
    return (
      <TouchInteraction
        onTap={handleTouchTap}
        onLongPress={handleTouchLongPress}
        className="dashboard-card-touch-wrapper"
      >
        {cardContent}
      </TouchInteraction>
    )
  }

  return cardContent
}

import React, { useState, useMemo } from 'react'
import { cn } from '@/lib/utils'
import { DashboardCard } from './dashboard-card'
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import {
  ChevronUp,
  ChevronDown,
  Search,
  Download,
  MoreHorizontal,
  ArrowUpDown,
} from 'lucide-react'
import { Skeleton } from '@/components/ui/skeleton'
import type { DashboardCardProps } from '@/types/dashboard'

interface DataTableColumn {
  key: string
  label: string
  sortable?: boolean
  searchable?: boolean
  render?: (value: any, row: any) => React.ReactNode
  className?: string
}

interface DataTableRow {
  id: string | number
  [key: string]: any
}

interface DataTableCardProps extends Omit<DashboardCardProps, 'metric'> {
  columns: DataTableColumn[]
  data: DataTableRow[]
  searchable?: boolean
  sortable?: boolean
  exportable?: boolean
  pageSize?: number
  emptyMessage?: string
  onRowClick?: (row: DataTableRow) => void
  onExport?: (format: 'csv' | 'json') => void
}

type SortDirection = 'asc' | 'desc' | null

export function DataTableCard({
  title,
  subtitle,
  icon,
  columns,
  data,
  searchable = true,
  sortable = true,
  exportable = false,
  pageSize = 10,
  emptyMessage = 'No data available',
  children,
  actions,
  loading = false,
  error,
  className,
  size = 'md',
  priority = 'medium',
  interactive = false,
  onClick,
  onFilter,
  onRowClick,
  onExport,
}: DataTableCardProps) {
  const [searchTerm, setSearchTerm] = useState('')
  const [sortColumn, setSortColumn] = useState<string | null>(null)
  const [sortDirection, setSortDirection] = useState<SortDirection>(null)
  const [currentPage, setCurrentPage] = useState(1)

  // Filter and sort data
  const processedData = useMemo(() => {
    let filtered = data

    // Apply search filter
    if (searchTerm && searchable) {
      const searchableColumns = columns.filter(col => col.searchable !== false)
      filtered = data.filter(row =>
        searchableColumns.some(col =>
          String(row[col.key]).toLowerCase().includes(searchTerm.toLowerCase())
        )
      )
    }

    // Apply sorting
    if (sortColumn && sortDirection && sortable) {
      filtered = [...filtered].sort((a, b) => {
        const aValue = a[sortColumn]
        const bValue = b[sortColumn]

        if (aValue === bValue) return 0

        const comparison = aValue < bValue ? -1 : 1
        return sortDirection === 'asc' ? comparison : -comparison
      })
    }

    return filtered
  }, [data, searchTerm, sortColumn, sortDirection, columns, searchable, sortable])

  // Paginate data
  const paginatedData = useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize
    return processedData.slice(startIndex, startIndex + pageSize)
  }, [processedData, currentPage, pageSize])

  const totalPages = Math.ceil(processedData.length / pageSize)

  const handleSort = (columnKey: string) => {
    if (!sortable) return

    const column = columns.find(col => col.key === columnKey)
    if (!column?.sortable) return

    if (sortColumn === columnKey) {
      setSortDirection(prev => 
        prev === 'asc' ? 'desc' : prev === 'desc' ? null : 'asc'
      )
      if (sortDirection === 'desc') {
        setSortColumn(null)
      }
    } else {
      setSortColumn(columnKey)
      setSortDirection('asc')
    }
  }

  const handleExport = (format: 'csv' | 'json') => {
    if (onExport) {
      onExport(format)
    } else {
      // Default export implementation
      const dataToExport = processedData
      const filename = `${title?.replace(/\s+/g, '_').toLowerCase() || 'data'}.${format}`

      if (format === 'csv') {
        const headers = columns.map(col => col.label).join(',')
        const rows = dataToExport.map(row =>
          columns.map(col => `"${row[col.key] || ''}"`).join(',')
        ).join('\n')
        const csv = `${headers}\n${rows}`

        const blob = new Blob([csv], { type: 'text/csv' })
        const url = URL.createObjectURL(blob)
        const a = document.createElement('a')
        a.href = url
        a.download = filename
        a.click()
        URL.revokeObjectURL(url)
      } else if (format === 'json') {
        const json = JSON.stringify(dataToExport, null, 2)
        const blob = new Blob([json], { type: 'application/json' })
        const url = URL.createObjectURL(blob)
        const a = document.createElement('a')
        a.href = url
        a.download = filename
        a.click()
        URL.revokeObjectURL(url)
      }
    }
  }

  const renderTableActions = () => {
    const tableActions = (
      <div className="flex items-center space-x-2">
        {searchable && (
          <div className="relative">
            <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-8 h-8 w-32"
            />
          </div>
        )}

        {exportable && (
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                className="h-8 w-8 p-0"
                aria-label="Export table data"
              >
                <Download className="h-4 w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem onClick={() => handleExport('csv')}>
                Export as CSV
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => handleExport('json')}>
                Export as JSON
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        )}

        {actions && (
          <>
            {(searchable || exportable) && (
              <div className="w-px h-4 bg-border" />
            )}
            {actions}
          </>
        )}
      </div>
    )

    return tableActions
  }

  const renderTable = () => {
    if (loading) {
      return (
        <div className="space-y-3">
          <div className="flex space-x-4">
            {columns.map((col, index) => (
              <Skeleton key={index} className="h-4 flex-1" />
            ))}
          </div>
          {Array.from({ length: 5 }).map((_, index) => (
            <div key={index} className="flex space-x-4">
              {columns.map((col, colIndex) => (
                <Skeleton key={colIndex} className="h-4 flex-1" />
              ))}
            </div>
          ))}
        </div>
      )
    }

    if (error) {
      return (
        <div className="flex items-center justify-center h-32 text-muted-foreground">
          <div className="text-center">
            <p className="text-sm font-medium">Unable to load table data</p>
            <p className="text-xs mt-1">{error}</p>
          </div>
        </div>
      )
    }

    if (paginatedData.length === 0) {
      return (
        <div className="flex items-center justify-center h-32 text-muted-foreground">
          <p className="text-sm">{emptyMessage}</p>
        </div>
      )
    }

    return (
      <div className="space-y-4">
        <div className="rounded-md border">
          <Table>
            <TableHeader>
              <TableRow>
                {columns.map((column) => (
                  <TableHead
                    key={column.key}
                    className={cn(
                      column.className,
                      sortable && column.sortable !== false && 'cursor-pointer hover:bg-muted/50'
                    )}
                    onClick={() => handleSort(column.key)}
                  >
                    <div className="flex items-center space-x-1">
                      <span>{column.label}</span>
                      {sortable && column.sortable !== false && (
                        <div className="flex flex-col">
                          {sortColumn === column.key ? (
                            sortDirection === 'asc' ? (
                              <ChevronUp className="h-3 w-3" />
                            ) : sortDirection === 'desc' ? (
                              <ChevronDown className="h-3 w-3" />
                            ) : (
                              <ArrowUpDown className="h-3 w-3 opacity-50" />
                            )
                          ) : (
                            <ArrowUpDown className="h-3 w-3 opacity-50" />
                          )}
                        </div>
                      )}
                    </div>
                  </TableHead>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {paginatedData.map((row) => (
                <TableRow
                  key={row.id}
                  className={cn(
                    onRowClick && 'cursor-pointer hover:bg-muted/50'
                  )}
                  onClick={() => onRowClick?.(row)}
                >
                  {columns.map((column) => (
                    <TableCell
                      key={column.key}
                      className={column.className}
                    >
                      {column.render
                        ? column.render(row[column.key], row)
                        : row[column.key]
                      }
                    </TableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>

        {totalPages > 1 && (
          <div className="flex items-center justify-between">
            <p className="text-sm text-muted-foreground">
              Showing {((currentPage - 1) * pageSize) + 1} to{' '}
              {Math.min(currentPage * pageSize, processedData.length)} of{' '}
              {processedData.length} entries
            </p>
            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                disabled={currentPage === 1}
              >
                Previous
              </Button>
              <span className="text-sm">
                Page {currentPage} of {totalPages}
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                disabled={currentPage === totalPages}
              >
                Next
              </Button>
            </div>
          </div>
        )}
      </div>
    )
  }

  return (
    <DashboardCard
      title={title}
      subtitle={subtitle}
      icon={icon}
      actions={renderTableActions()}
      loading={false} // Handle loading internally
      error={undefined} // Handle error internally
      className={cn('data-table-card', className)}
      size={size}
      priority={priority}
      interactive={interactive}
      onClick={onClick}
      onFilter={onFilter}
    >
      <div className="data-table-content">
        {renderTable()}
        {children}
      </div>
    </DashboardCard>
  )
}

export type { DataTableColumn, DataTableRow, DataTableCardProps }

import { renderHook, act } from '@testing-library/react'
import {
  ChartConfigBuilder,
  ChartConfigUtils,
  useChartConfig,
  CHART_THEMES,
  CHART_PRESETS,
} from '../chart-config-system'

describe('ChartConfigBuilder', () => {
  it('creates a builder with default configuration', () => {
    const builder = new ChartConfigBuilder()
    const config = builder.build()

    expect(config.colors.primary).toBe('#3F51B5')
    expect(config.legend.show).toBe(true)
    expect(config.tooltip.show).toBe(true)
    expect(config.animations.enabled).toBe(true)
  })

  it('creates a builder with base configuration', () => {
    const baseConfig = {
      colors: { primary: '#FF0000' },
      legend: { show: false },
    }

    const builder = new ChartConfigBuilder(baseConfig)
    const config = builder.build()

    expect(config.colors.primary).toBe('#FF0000')
    expect(config.legend.show).toBe(false)
  })

  it('applies theme correctly', () => {
    const builder = new ChartConfigBuilder()
    const config = builder.withTheme('dark').build()

    expect(config.colors.primary).toBe(CHART_THEMES.dark.colors.primary)
    expect(config.colors.secondary).toBe(CHART_THEMES.dark.colors.secondary)
  })

  it('applies preset correctly', () => {
    const builder = new ChartConfigBuilder()
    const config = builder.withPreset('pie').build()

    expect(config.legend.position).toBe('right')
    expect(config.animations.duration).toBe(500)
  })

  it('chains configuration methods', () => {
    const builder = new ChartConfigBuilder()
    const config = builder
      .withTheme('vibrant')
      .withPreset('bar')
      .withColors({ primary: '#CUSTOM' })
      .withLegend({ show: false })
      .withTooltip({ show: false })
      .withAxes({ x: { show: false } })
      .withAnimations({ enabled: false })
      .build()

    expect(config.colors.primary).toBe('#CUSTOM')
    expect(config.legend.show).toBe(false)
    expect(config.tooltip.show).toBe(false)
    expect(config.axes.x.show).toBe(false)
    expect(config.animations.enabled).toBe(false)
  })

  it('preserves colors when applying preset', () => {
    const builder = new ChartConfigBuilder()
    const config = builder
      .withTheme('dark')
      .withPreset('line')
      .build()

    expect(config.colors.primary).toBe(CHART_THEMES.dark.colors.primary)
    expect(config.legend.position).toBe('bottom') // From preset
  })
})

describe('ChartConfigUtils', () => {
  it('creates a builder', () => {
    const builder = ChartConfigUtils.builder()
    expect(builder).toBeInstanceOf(ChartConfigBuilder)
  })

  it('gets theme palette', () => {
    const palette = ChartConfigUtils.getThemePalette('vibrant')
    expect(palette).toEqual(CHART_THEMES.vibrant.palette)
  })

  it('creates quick configuration', () => {
    const config = ChartConfigUtils.quickConfig('pie', 'minimal')

    expect(config.colors.primary).toBe(CHART_THEMES.minimal.colors.primary)
    expect(config.legend.position).toBe('right') // From pie preset
    expect(config.animations.duration).toBe(500) // From pie preset
  })

  it('merges multiple configurations', () => {
    const config1 = { colors: { primary: '#FF0000' } }
    const config2 = { legend: { show: false } }
    const config3 = { tooltip: { show: false } }

    const merged = ChartConfigUtils.merge(config1, config2, config3)

    expect(merged.colors.primary).toBe('#FF0000')
    expect(merged.legend.show).toBe(false)
    expect(merged.tooltip.show).toBe(false)
  })

  it('validates configuration', () => {
    const validConfig = ChartConfigUtils.quickConfig('line')
    const validation = ChartConfigUtils.validate(validConfig)

    expect(validation.valid).toBe(true)
    expect(validation.errors).toHaveLength(0)
  })

  it('validates incomplete configuration', () => {
    const incompleteConfig = {} as any
    const validation = ChartConfigUtils.validate(incompleteConfig)

    expect(validation.valid).toBe(false)
    expect(validation.errors.length).toBeGreaterThan(0)
    expect(validation.errors).toContain('Colors configuration is required')
  })
})

describe('useChartConfig', () => {
  it('initializes with default configuration', () => {
    const { result } = renderHook(() => useChartConfig())

    expect(result.current.config.colors.primary).toBe('#3F51B5')
    expect(result.current.config.legend.show).toBe(true)
  })

  it('initializes with custom configuration', () => {
    const initialConfig = {
      colors: { primary: '#FF0000' },
      legend: { show: false },
    }

    const { result } = renderHook(() => useChartConfig(initialConfig))

    expect(result.current.config.colors.primary).toBe('#FF0000')
    expect(result.current.config.legend.show).toBe(false)
  })

  it('updates configuration', () => {
    const { result } = renderHook(() => useChartConfig())

    act(() => {
      result.current.updateConfig({
        colors: { primary: '#00FF00' },
        legend: { show: false },
      })
    })

    expect(result.current.config.colors.primary).toBe('#00FF00')
    expect(result.current.config.legend.show).toBe(false)
  })

  it('resets configuration', () => {
    const initialConfig = {
      colors: { primary: '#FF0000' },
    }

    const { result } = renderHook(() => useChartConfig(initialConfig))

    act(() => {
      result.current.updateConfig({
        colors: { primary: '#00FF00' },
      })
    })

    expect(result.current.config.colors.primary).toBe('#00FF00')

    act(() => {
      result.current.resetConfig()
    })

    expect(result.current.config.colors.primary).toBe('#FF0000')
  })

  it('applies theme', () => {
    const { result } = renderHook(() => useChartConfig())

    act(() => {
      result.current.applyTheme('dark')
    })

    expect(result.current.config.colors.primary).toBe(CHART_THEMES.dark.colors.primary)
  })

  it('applies preset', () => {
    const { result } = renderHook(() => useChartConfig())

    act(() => {
      result.current.applyPreset('pie')
    })

    expect(result.current.config.legend.position).toBe('right')
    expect(result.current.config.animations.duration).toBe(500)
  })

  it('preserves custom colors when applying preset', () => {
    const { result } = renderHook(() => useChartConfig())

    act(() => {
      result.current.updateConfig({
        colors: { primary: '#CUSTOM' },
      })
    })

    act(() => {
      result.current.applyPreset('bar')
    })

    expect(result.current.config.colors.primary).toBe('#CUSTOM')
    expect(result.current.config.animations.duration).toBe(400) // From bar preset
  })
})

describe('CHART_THEMES', () => {
