    render(<BarChart {...defaultProps} />)

    expect(screen.getByText('YAxis (formatted)')).toBeInTheDocument()
  })

  it('hides axes when configured', () => {
    const configWithHiddenAxes = {
      ...mockConfig,
      axes: {
        x: { show: false },
        y: { show: false },
      },
    }

    render(<BarChart {...defaultProps} config={configWithHiddenAxes} />)

    expect(screen.queryByTestId('x-axis')).not.toBeInTheDocument()
    expect(screen.queryByTestId('y-axis')).not.toBeInTheDocument()
  })

  it('hides tooltip when configured', () => {
    const configWithHiddenTooltip = {
      ...mockConfig,
      tooltip: { show: false },
    }

    render(<BarChart {...defaultProps} config={configWithHiddenTooltip} />)

    expect(screen.queryByTestId('tooltip')).not.toBeInTheDocument()
  })

  it('hides legend when configured', () => {
    const configWithHiddenLegend = {
      ...mockConfig,
      legend: { show: false, position: 'bottom' as const, align: 'center' as const },
    }

    render(<BarChart {...defaultProps} config={configWithHiddenLegend} />)

    expect(screen.queryByTestId('legend')).not.toBeInTheDocument()
  })

  it('handles empty data gracefully', () => {
    const emptyData: ChartData = { datasets: [] }

    render(<BarChart {...defaultProps} data={emptyData} />)

    expect(screen.getByTestId('recharts-bar-chart')).toBeInTheDocument()

    const chartDataElement = screen.getByTestId('chart-data')
    const chartData = JSON.parse(chartDataElement.textContent || '[]')
    expect(chartData).toHaveLength(0)
  })

  it('sorts categories correctly', () => {
    const unsortedData: ChartData = {
      datasets: [
        {
          label: 'Test',
          data: [
            { x: 'Q3', y: 150 },
            { x: 'Q1', y: 100 },
            { x: 'Q4', y: 300 },
            { x: 'Q2', y: 200 },
          ],
        },
      ],
    }

    render(<BarChart {...defaultProps} data={unsortedData} />)

    const chartDataElement = screen.getByTestId('chart-data')
    const chartData = JSON.parse(chartDataElement.textContent || '[]')

    expect(chartData[0].x).toBe('Q1')
    expect(chartData[1].x).toBe('Q2')
    expect(chartData[2].x).toBe('Q3')
    expect(chartData[3].x).toBe('Q4')
  })

  it('handles numeric categories', () => {
    const numericData: ChartData = {
      datasets: [
        {
          label: 'Test',
          data: [
            { x: 2023, y: 150 },
            { x: 2021, y: 100 },
            { x: 2022, y: 200 },
          ],
        },
      ],
    }

    render(<BarChart {...defaultProps} data={numericData} />)

    const chartDataElement = screen.getByTestId('chart-data')
    const chartData = JSON.parse(chartDataElement.textContent || '[]')

    expect(chartData[0].x).toBe(2021)
    expect(chartData[1].x).toBe(2022)
    expect(chartData[2].x).toBe(2023)
  })

  it('handles missing data points', () => {
    const sparseData: ChartData = {
      datasets: [
        {
          label: 'Dataset 1',
          data: [
            { x: 'Q1', y: 100 },
            { x: 'Q3', y: 150 },
          ],
        },
        {
          label: 'Dataset 2',
          data: [
            { x: 'Q1', y: 80 },
            { x: 'Q2', y: 180 },
            { x: 'Q3', y: 120 },
          ],
        },
      ],
    }

    render(<BarChart {...defaultProps} data={sparseData} />)

    const chartDataElement = screen.getByTestId('chart-data')
    const chartData = JSON.parse(chartDataElement.textContent || '[]')

    expect(chartData).toHaveLength(3) // Q1, Q2, Q3
    expect(chartData[0]).toEqual({
      x: 'Q1',
      'Dataset 1': 100,
      'Dataset 2': 80,
    })
    expect(chartData[1]).toEqual({
      x: 'Q2',
      'Dataset 1': 0, // Missing data should default to 0
      'Dataset 2': 180,
    })
    expect(chartData[2]).toEqual({
      x: 'Q3',
      'Dataset 1': 150,
      'Dataset 2': 120,
    })
  })
})

import React from 'react'
import { render, screen, fireEvent } from '@testing-library/react'
import userEvent from '@testing-library/user-event'
import { HeatmapChart } from '../heatmap-chart'
import type { ChartData, ChartConfiguration } from '@/types/dashboard'

// Mock Recharts ResponsiveContainer
jest.mock('recharts', () => ({
  ResponsiveContainer: ({ children }: any) => (
    <div data-testid="responsive-container">{children}</div>
  ),
}))

const mockData: ChartData = {
  datasets: [
    {
      label: 'Heatmap Data',
      data: [
        { x: 'Mon', y: 'Morning', value: 10 },
        { x: 'Mon', y: 'Afternoon', value: 20 },
        { x: 'Mon', y: 'Evening', value: 15 },
        { x: 'Tue', y: 'Morning', value: 25 },
        { x: 'Tue', y: 'Afternoon', value: 30 },
        { x: 'Tue', y: 'Evening', value: 18 },
        { x: 'Wed', y: 'Morning', value: 12 },
        { x: 'Wed', y: 'Afternoon', value: 22 },
        { x: 'Wed', y: 'Evening', value: 28 },
      ],
    },
  ],
  metadata: {
    title: 'Weekly Activity Heatmap',
  },
}

const mockConfig: ChartConfiguration = {
  colors: {
    primary: '#3F51B5',
    secondary: '#B19ADA',
    accent: '#FF6B35',
    success: '#10B981',
    warning: '#F59E0B',
    error: '#EF4444',
  },
  legend: {
    show: true,
    position: 'bottom',
    align: 'center',
  },
  tooltip: {
    show: true,
    format: (value: number) => `${value} units`,
  },
}

describe('HeatmapChart', () => {
  const defaultProps = {
    data: mockData,
    config: mockConfig,
    height: 300,
    responsive: true,
    interactive: true,
    exportable: false,
    colors: ['#3F51B5'],
  }

  it('renders heatmap chart correctly', () => {
    render(<HeatmapChart {...defaultProps} />)

    expect(screen.getByTestId('responsive-container')).toBeInTheDocument()
    expect(screen.getByText('Weekly Activity Heatmap')).toBeInTheDocument()
  })

  it('renders axis labels correctly', () => {
    render(<HeatmapChart {...defaultProps} />)

    // X-axis labels
    expect(screen.getByText('Mon')).toBeInTheDocument()
    expect(screen.getByText('Tue')).toBeInTheDocument()
    expect(screen.getByText('Wed')).toBeInTheDocument()

    // Y-axis labels
    expect(screen.getByText('Morning')).toBeInTheDocument()
    expect(screen.getByText('Afternoon')).toBeInTheDocument()
    expect(screen.getByText('Evening')).toBeInTheDocument()
  })

  it('sorts axis labels correctly', () => {
    const unsortedData: ChartData = {
      datasets: [
        {
          label: 'Test',
          data: [
            { x: 'Wed', y: 'Evening', value: 10 },
            { x: 'Mon', y: 'Morning', value: 20 },
            { x: 'Tue', y: 'Afternoon', value: 15 },
          ],
        },
      ],
    }

    render(<HeatmapChart {...defaultProps} data={unsortedData} />)

    // Labels should be sorted alphabetically
    const xLabels = screen.getAllByText(/Mon|Tue|Wed/)
    const yLabels = screen.getAllByText(/Morning|Afternoon|Evening/)

    expect(xLabels.length).toBeGreaterThan(0)
    expect(yLabels.length).toBeGreaterThan(0)
  })

  it('handles numeric axis values', () => {
    const numericData: ChartData = {
      datasets: [
        {
          label: 'Numeric Data',
          data: [
            { x: 2023, y: 1, value: 10 },
            { x: 2022, y: 2, value: 20 },
            { x: 2021, y: 3, value: 15 },
          ],
        },
      ],
    }

    render(<HeatmapChart {...defaultProps} data={numericData} />)

    expect(screen.getByText('2021')).toBeInTheDocument()
    expect(screen.getByText('2022')).toBeInTheDocument()
    expect(screen.getByText('2023')).toBeInTheDocument()
    expect(screen.getByText('1')).toBeInTheDocument()
    expect(screen.getByText('2')).toBeInTheDocument()
    expect(screen.getByText('3')).toBeInTheDocument()
  })

  it('handles cell click events', async () => {
    const user = userEvent.setup()
    const onDataClick = jest.fn()

    render(<HeatmapChart {...defaultProps} onDataClick={onDataClick} />)

    // Find and click a cell (SVG rect element)
    const cells = document.querySelectorAll('rect')
    expect(cells.length).toBeGreaterThan(0)

    fireEvent.click(cells[0])

    expect(onDataClick).toHaveBeenCalled()
  })

  it('shows tooltip on mouse enter', async () => {
    render(<HeatmapChart {...defaultProps} />)

    const cells = document.querySelectorAll('rect')
    expect(cells.length).toBeGreaterThan(0)

    // Simulate mouse enter
    fireEvent.mouseEnter(cells[0], {
      clientX: 100,
      clientY: 100,
    })

    // Tooltip should appear (though we can't easily test the positioning)
    // The tooltip is rendered conditionally based on state
  })

  it('hides tooltip on mouse leave', async () => {
    render(<HeatmapChart {...defaultProps} />)

    const cells = document.querySelectorAll('rect')
    expect(cells.length).toBeGreaterThan(0)

    // Simulate mouse enter then leave
    fireEvent.mouseEnter(cells[0], {
      clientX: 100,
      clientY: 100,
    })
    fireEvent.mouseLeave(cells[0])

    // Tooltip should be hidden
  })

  it('renders color legend when enabled', () => {
    render(<HeatmapChart {...defaultProps} />)

    // Should show min and max values in legend (with formatter applied)
    expect(screen.getByText('10 units')).toBeInTheDocument() // Min value
    expect(screen.getByText('30 units')).toBeInTheDocument() // Max value
  })

  it('hides color legend when disabled', () => {
    const configWithoutLegend = {
      ...mockConfig,
      legend: { show: false, position: 'bottom' as const, align: 'center' as const },
    }

    render(<HeatmapChart {...defaultProps} config={configWithoutLegend} />)

    // Legend should not be visible
    // This is harder to test directly, but the component should not render the legend div
  })

  it('applies custom color based on value', () => {
    render(<HeatmapChart {...defaultProps} />)

    const cells = document.querySelectorAll('rect')
    expect(cells.length).toBeGreaterThan(0)

    // Each cell should have a fill color
    cells.forEach(cell => {
      expect(cell.getAttribute('fill')).toBeTruthy()
    })
  })

  it('handles empty data gracefully', () => {
    const emptyData: ChartData = { datasets: [] }

    render(<HeatmapChart {...defaultProps} data={emptyData} />)

    expect(screen.getByTestId('responsive-container')).toBeInTheDocument()

    // Should not render any cells
    const cells = document.querySelectorAll('rect')
    expect(cells.length).toBe(0)
  })

  it('handles data without x or y values', () => {
    const invalidData: ChartData = {
      datasets: [
        {
          label: 'Invalid Data',
          data: [
            { value: 10 }, // Missing x and y
            { x: 'A', value: 20 }, // Missing y
            { y: 'B', value: 15 }, // Missing x
          ],
        },
      ],
    }

    render(<HeatmapChart {...defaultProps} data={invalidData} />)

    expect(screen.getByTestId('responsive-container')).toBeInTheDocument()

    // Should handle gracefully without crashing
  })

  it('calculates min and max values correctly', () => {
    render(<HeatmapChart {...defaultProps} />)

    // Min value should be 10, max should be 30 based on mock data (with formatter applied)
    expect(screen.getByText('10 units')).toBeInTheDocument()
    expect(screen.getByText('30 units')).toBeInTheDocument()
  })

  it('handles single value data', () => {
    const singleValueData: ChartData = {
      datasets: [
        {
          label: 'Single Value',
          data: [
            { x: 'A', y: 'B', value: 50 },
          ],
        },
      ],
    }

    render(<HeatmapChart {...defaultProps} data={singleValueData} />)

    expect(screen.getByText('A')).toBeInTheDocument()
    expect(screen.getByText('B')).toBeInTheDocument()
    expect(screen.getAllByText('50 units')).toHaveLength(2) // Min and max are the same
  })

  it('applies tooltip formatter when provided', () => {
    render(<HeatmapChart {...defaultProps} />)

    const cells = document.querySelectorAll('rect')
    expect(cells.length).toBeGreaterThan(0)

    // Simulate mouse enter to show tooltip
    fireEvent.mouseEnter(cells[0], {
      clientX: 100,
      clientY: 100,
    })

    // The tooltip should use the formatter (though hard to test the exact content)
  })

  it('handles missing values in sparse data', () => {
    const sparseData: ChartData = {
      datasets: [
        {
          label: 'Sparse Data',
          data: [
            { x: 'A', y: '1', value: 10 },
            { x: 'C', y: '1', value: 20 }, // Missing B,1
            { x: 'A', y: '3', value: 15 }, // Missing A,2
          ],
        },
      ],
    }

    render(<HeatmapChart {...defaultProps} data={sparseData} />)

    expect(screen.getByText('A')).toBeInTheDocument()
    expect(screen.getByText('C')).toBeInTheDocument()
    expect(screen.getByText('1')).toBeInTheDocument()
    expect(screen.getByText('3')).toBeInTheDocument()

    // Should render cells for all combinations, with 0 values for missing data
    const cells = document.querySelectorAll('rect')
    expect(cells.length).toBeGreaterThan(0)
  })

  it('renders without title when not provided', () => {
    const dataWithoutTitle: ChartData = {
      datasets: [
        {
          label: 'Test',
          data: [{ x: 'A', y: 'B', value: 10 }],
        },
      ],
    }

    render(<HeatmapChart {...defaultProps} data={dataWithoutTitle} />)

    expect(screen.queryByText('Weekly Activity Heatmap')).not.toBeInTheDocument()
    expect(screen.getByTestId('responsive-container')).toBeInTheDocument()
  })
})

import React from 'react'
import { render, screen } from '@testing-library/react'
import userEvent from '@testing-library/user-event'
import { LineChart } from '../line-chart'
import type { ChartData, ChartConfiguration } from '@/types/dashboard'

// Mock Recharts components
jest.mock('recharts', () => ({
  LineChart: ({ children, data }: any) => (
    <div data-testid="recharts-line-chart">
      {children}
      <div data-testid="chart-data">{JSON.stringify(data)}</div>
    </div>
  ),
  Line: ({ dataKey, onClick }: any) => (
    <div data-testid={`line-${dataKey}`} onClick={() => onClick?.({ value: 100 }, 0)}>
      Line: {dataKey}
    </div>
  ),
  XAxis: ({ dataKey }: any) => <div data-testid="x-axis">XAxis: {dataKey}</div>,
  YAxis: () => <div data-testid="y-axis">YAxis</div>,
  CartesianGrid: () => <div data-testid="cartesian-grid">Grid</div>,
  Tooltip: ({ content }: any) => <div data-testid="tooltip">Tooltip</div>,
  Legend: ({ onClick }: any) => (
    <div data-testid="legend" onClick={() => onClick?.({ value: 'Test', color: '#000' })}>
      Legend
    </div>
  ),
  ResponsiveContainer: ({ children }: any) => (
    <div data-testid="responsive-container">{children}</div>
  ),
  Area: ({ dataKey, onClick }: any) => (
    <div data-testid={`area-${dataKey}`} onClick={() => onClick?.({ value: 100 }, 0)}>
      Area: {dataKey}
    </div>
  ),
  AreaChart: ({ children, data }: any) => (
    <div data-testid="recharts-area-chart">
      {children}
      <div data-testid="chart-data">{JSON.stringify(data)}</div>
    </div>
  ),
  Scatter: ({ dataKey, onClick }: any) => (
    <div data-testid={`scatter-${dataKey}`} onClick={() => onClick?.({ value: 100 }, 0)}>
      Scatter: {dataKey}
    </div>
  ),
  ScatterChart: ({ children, data }: any) => (
    <div data-testid="recharts-scatter-chart">
      {children}
      <div data-testid="chart-data">{JSON.stringify(data)}</div>
    </div>
  ),
}))

const mockData: ChartData = {
  datasets: [
    {
      label: 'Dataset 1',
      data: [
        { x: 'Jan', y: 100 },
        { x: 'Feb', y: 200 },
        { x: 'Mar', y: 150 },
      ],
    },
    {
      label: 'Dataset 2',
      data: [
        { x: 'Jan', y: 80 },
        { x: 'Feb', y: 180 },
        { x: 'Mar', y: 120 },
      ],
    },
  ],
}

const mockConfig: ChartConfiguration = {
  colors: {
    primary: '#3F51B5',
    secondary: '#B19ADA',
    accent: '#FF6B35',
    success: '#10B981',
    warning: '#F59E0B',
    error: '#EF4444',
  },
  legend: {
    show: true,
    position: 'bottom',
    align: 'center',
  },
  tooltip: {
    show: true,
  },
  axes: {
    x: { show: true },
    y: { show: true },
  },
  animations: {
    enabled: true,
    duration: 300,
    easing: 'ease-out',
  },
}

describe('LineChart', () => {
  const defaultProps = {
    data: mockData,
    config: mockConfig,
    height: 300,
    responsive: true,
    interactive: true,
    exportable: false,
    colors: ['#3F51B5', '#B19ADA'],
  }

  it('renders line chart correctly', () => {
    render(<LineChart {...defaultProps} />)

    expect(screen.getByTestId('recharts-line-chart')).toBeInTheDocument()
    expect(screen.getByTestId('responsive-container')).toBeInTheDocument()
    expect(screen.getByTestId('x-axis')).toBeInTheDocument()
    expect(screen.getByTestId('y-axis')).toBeInTheDocument()
    expect(screen.getByTestId('cartesian-grid')).toBeInTheDocument()
    expect(screen.getByTestId('tooltip')).toBeInTheDocument()
    expect(screen.getByTestId('legend')).toBeInTheDocument()
  })

  it('renders multiple datasets as lines', () => {
    render(<LineChart {...defaultProps} />)

    expect(screen.getByTestId('line-Dataset 1')).toBeInTheDocument()
    expect(screen.getByTestId('line-Dataset 2')).toBeInTheDocument()
    expect(screen.getByText('Line: Dataset 1')).toBeInTheDocument()
    expect(screen.getByText('Line: Dataset 2')).toBeInTheDocument()
  })

  it('transforms data correctly for Recharts', () => {
    render(<LineChart {...defaultProps} />)

    const chartDataElement = screen.getByTestId('chart-data')
    const chartData = JSON.parse(chartDataElement.textContent || '[]')

    expect(chartData).toHaveLength(3) // Jan, Feb, Mar
    expect(chartData[0]).toEqual({
      x: 'Feb',
      'Dataset 1': 200,
      'Dataset 2': 180,
    })
    expect(chartData[1]).toEqual({
      x: 'Jan',
      'Dataset 1': 100,
      'Dataset 2': 80,
    })
    expect(chartData[2]).toEqual({
      x: 'Mar',
      'Dataset 1': 150,
      'Dataset 2': 120,
    })
  })

  it('handles data click events', async () => {
    const user = userEvent.setup()
    const onDataClick = jest.fn()

    render(<LineChart {...defaultProps} onDataClick={onDataClick} />)

    await user.click(screen.getByTestId('line-Dataset 1'))

    expect(onDataClick).toHaveBeenCalledWith({
      x: undefined,
      y: 100,
      value: 100,
      label: undefined,
    })
  })

  it('handles legend click events', async () => {
    const user = userEvent.setup()
    const onLegendClick = jest.fn()

    render(<LineChart {...defaultProps} onLegendClick={onLegendClick} />)

    await user.click(screen.getByTestId('legend'))

    expect(onLegendClick).toHaveBeenCalledWith({
      label: 'Test',
      color: '#000',
      visible: true,
    })
  })

  it('renders as area chart when area prop is true', () => {
    render(<LineChart {...defaultProps} area={true} />)

    expect(screen.getByTestId('recharts-area-chart')).toBeInTheDocument()
    expect(screen.getByTestId('area-Dataset 1')).toBeInTheDocument()
    expect(screen.getByTestId('area-Dataset 2')).toBeInTheDocument()
  })

  it('renders as scatter chart when scatter prop is true', () => {
    render(<LineChart {...defaultProps} scatter={true} />)

    expect(screen.getByTestId('recharts-scatter-chart')).toBeInTheDocument()
    expect(screen.getByTestId('scatter-Dataset 1')).toBeInTheDocument()
    expect(screen.getByTestId('scatter-Dataset 2')).toBeInTheDocument()
  })

  it('hides axes when configured', () => {
    const configWithHiddenAxes = {
      ...mockConfig,
      axes: {
        x: { show: false },
        y: { show: false },
      },
    }

    render(<LineChart {...defaultProps} config={configWithHiddenAxes} />)

    expect(screen.queryByTestId('x-axis')).not.toBeInTheDocument()
    expect(screen.queryByTestId('y-axis')).not.toBeInTheDocument()
  })

  it('hides tooltip when configured', () => {
    const configWithHiddenTooltip = {
      ...mockConfig,
      tooltip: { show: false },
    }

    render(<LineChart {...defaultProps} config={configWithHiddenTooltip} />)

    expect(screen.queryByTestId('tooltip')).not.toBeInTheDocument()
  })

  it('hides legend when configured', () => {
    const configWithHiddenLegend = {
      ...mockConfig,
      legend: { show: false, position: 'bottom' as const, align: 'center' as const },
    }

    render(<LineChart {...defaultProps} config={configWithHiddenLegend} />)

    expect(screen.queryByTestId('legend')).not.toBeInTheDocument()
  })

  it('handles empty data gracefully', () => {
    const emptyData: ChartData = { datasets: [] }

    render(<LineChart {...defaultProps} data={emptyData} />)

    expect(screen.getByTestId('recharts-line-chart')).toBeInTheDocument()

    const chartDataElement = screen.getByTestId('chart-data')
    const chartData = JSON.parse(chartDataElement.textContent || '[]')
    expect(chartData).toHaveLength(0)
  })

  it('sorts x values correctly', () => {
    const unsortedData: ChartData = {
      datasets: [
        {
          label: 'Test',
          data: [
            { x: 'Mar', y: 150 },
            { x: 'Jan', y: 100 },
            { x: 'Feb', y: 200 },
          ],
        },
      ],
    }

    render(<LineChart {...defaultProps} data={unsortedData} />)

    const chartDataElement = screen.getByTestId('chart-data')
    const chartData = JSON.parse(chartDataElement.textContent || '[]')

    // String sorting is alphabetical: Feb, Jan, Mar
    expect(chartData[0].x).toBe('Feb')
    expect(chartData[1].x).toBe('Jan')
    expect(chartData[2].x).toBe('Mar')
  })

  it('handles numeric x values', () => {
    const numericData: ChartData = {
      datasets: [
        {
          label: 'Test',
          data: [
            { x: 3, y: 150 },
            { x: 1, y: 100 },
            { x: 2, y: 200 },
          ],
        },
      ],
    }

    render(<LineChart {...defaultProps} data={numericData} />)

    const chartDataElement = screen.getByTestId('chart-data')
    const chartData = JSON.parse(chartDataElement.textContent || '[]')

    expect(chartData[0].x).toBe(1)
    expect(chartData[1].x).toBe(2)
    expect(chartData[2].x).toBe(3)
  })
})

import React from 'react'
import { render, screen } from '@testing-library/react'
import userEvent from '@testing-library/user-event'
import { PieChart } from '../pie-chart'
import type { ChartData, ChartConfiguration } from '@/types/dashboard'

// Mock Recharts components
jest.mock('recharts', () => ({
  PieChart: ({ children }: any) => (
    <div data-testid="recharts-pie-chart">{children}</div>
  ),
  Pie: ({ data, onClick, label }: any) => (
    <div data-testid="pie">
      <div data-testid="pie-data">{JSON.stringify(data)}</div>
      {data.map((item: any, index: number) => (
        <div
          key={index}
          data-testid={`pie-slice-${index}`}
          onClick={() => onClick?.(item, index)}
        >
          Slice: {item.name} ({item.value})
        </div>
      ))}
    </div>
  ),
  Cell: ({ fill }: any) => <div data-testid="pie-cell" style={{ backgroundColor: fill }} />,
  Tooltip: ({ content }: any) => <div data-testid="tooltip">Tooltip</div>,
  Legend: ({ content }: any) => <div data-testid="legend">Legend</div>,
  ResponsiveContainer: ({ children }: any) => (
    <div data-testid="responsive-container">{children}</div>
  ),
}))

const mockData: ChartData = {
  datasets: [
    {
      label: 'Categories',
      data: [
        { label: 'Category A', value: 100, color: '#3F51B5' },
        { label: 'Category B', value: 200, color: '#B19ADA' },
        { label: 'Category C', value: 150, color: '#FF6B35' },
        { label: 'Category D', value: 50, color: '#10B981' },
      ],
    },
  ],
}

const mockConfig: ChartConfiguration = {
  colors: {
    primary: '#3F51B5',
    secondary: '#B19ADA',
    accent: '#FF6B35',
    success: '#10B981',
    warning: '#F59E0B',
    error: '#EF4444',
  },
  legend: {
    show: true,
    position: 'bottom',
    align: 'center',
  },
  tooltip: {
    show: true,
  },
  animations: {
    enabled: true,
    duration: 300,
    easing: 'ease-out',
  },
}

describe('PieChart', () => {
  const defaultProps = {
    data: mockData,
    config: mockConfig,
    height: 300,
    responsive: true,
    interactive: true,
    exportable: false,
    colors: ['#3F51B5', '#B19ADA', '#FF6B35', '#10B981'],
  }

  it('renders pie chart correctly', () => {
    render(<PieChart {...defaultProps} />)

    expect(screen.getByTestId('recharts-pie-chart')).toBeInTheDocument()
    expect(screen.getByTestId('responsive-container')).toBeInTheDocument()
    expect(screen.getByTestId('pie')).toBeInTheDocument()
    expect(screen.getByTestId('tooltip')).toBeInTheDocument()
    expect(screen.getByTestId('legend')).toBeInTheDocument()
  })

  it('transforms data correctly for pie chart', () => {
    render(<PieChart {...defaultProps} />)

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')

    expect(pieData).toHaveLength(4)
    expect(pieData[0]).toEqual({
      name: 'Category A',
      value: 100,
      color: '#3F51B5',
      originalData: mockData.datasets[0].data[0],
    })
    expect(pieData[1]).toEqual({
      name: 'Category B',
      value: 200,
      color: '#B19ADA',
      originalData: mockData.datasets[0].data[1],
    })
  })

  it('renders pie slices with correct data', () => {
    render(<PieChart {...defaultProps} />)

    expect(screen.getByText('Slice: Category A (100)')).toBeInTheDocument()
    expect(screen.getByText('Slice: Category B (200)')).toBeInTheDocument()
    expect(screen.getByText('Slice: Category C (150)')).toBeInTheDocument()
    expect(screen.getByText('Slice: Category D (50)')).toBeInTheDocument()
  })

  it('handles slice click events', async () => {
    const user = userEvent.setup()
    const onDataClick = jest.fn()

    render(<PieChart {...defaultProps} onDataClick={onDataClick} />)

    await user.click(screen.getByTestId('pie-slice-0'))

    expect(onDataClick).toHaveBeenCalledWith({
      x: 'Category A',
      y: 100,
      value: 100,
      label: 'Category A',
      color: '#3F51B5',
      metadata: mockData.datasets[0].data[0].metadata,
    })
  })

  it('uses fallback colors when data colors are not provided', () => {
    const dataWithoutColors: ChartData = {
      datasets: [
        {
          label: 'Categories',
          data: [
            { label: 'Category A', value: 100 },
            { label: 'Category B', value: 200 },
          ],
        },
      ],
    }

    render(<PieChart {...defaultProps} data={dataWithoutColors} />)

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')

    expect(pieData[0].color).toBe('#3F51B5') // First color from props
    expect(pieData[1].color).toBe('#B19ADA') // Second color from props
  })

  it('generates labels from x values when label is not provided', () => {
    const dataWithXValues: ChartData = {
      datasets: [
        {
          label: 'Categories',
          data: [
            { x: 'A', value: 100 },
            { x: 'B', value: 200 },
          ],
        },
      ],
    }

    render(<PieChart {...defaultProps} data={dataWithXValues} />)

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')

    expect(pieData[0].name).toBe('A')
    expect(pieData[1].name).toBe('B')
  })

  it('generates fallback labels when neither label nor x is provided', () => {
    const dataWithoutLabels: ChartData = {
      datasets: [
        {
          label: 'Categories',
          data: [
            { value: 100 },
            { value: 200 },
          ],
        },
      ],
    }

    render(<PieChart {...defaultProps} data={dataWithoutLabels} />)

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')

    expect(pieData[0].name).toBe('Item 1')
    expect(pieData[1].name).toBe('Item 2')
  })

  it('uses y value when value is not provided', () => {
    const dataWithYValues: ChartData = {
      datasets: [
        {
          label: 'Categories',
          data: [
            { label: 'Category A', y: 100 },
            { label: 'Category B', y: 200 },
          ],
        },
      ],
    }

    render(<PieChart {...defaultProps} data={dataWithYValues} />)

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')

    expect(pieData[0].value).toBe(100)
    expect(pieData[1].value).toBe(200)
  })

  it('hides tooltip when configured', () => {
    const configWithHiddenTooltip = {
      ...mockConfig,
      tooltip: { show: false },
    }

    render(<PieChart {...defaultProps} config={configWithHiddenTooltip} />)

    expect(screen.queryByTestId('tooltip')).not.toBeInTheDocument()
  })

  it('hides legend when configured', () => {
    const configWithHiddenLegend = {
      ...mockConfig,
      legend: { show: false, position: 'bottom' as const, align: 'center' as const },
    }

    render(<PieChart {...defaultProps} config={configWithHiddenLegend} />)

    expect(screen.queryByTestId('legend')).not.toBeInTheDocument()
  })

  it('handles empty data gracefully', () => {
    const emptyData: ChartData = { datasets: [] }

    render(<PieChart {...defaultProps} data={emptyData} />)

    expect(screen.getByTestId('recharts-pie-chart')).toBeInTheDocument()

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')
    expect(pieData).toHaveLength(0)
  })

  it('handles dataset without data', () => {
    const datasetWithoutData: ChartData = {
      datasets: [
        {
          label: 'Empty Dataset',
          data: [],
        },
      ],
    }

    render(<PieChart {...defaultProps} data={datasetWithoutData} />)

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')
    expect(pieData).toHaveLength(0)
  })

  it('handles zero values', () => {
    const dataWithZeros: ChartData = {
      datasets: [
        {
          label: 'Categories',
          data: [
            { label: 'Category A', value: 0 },
            { label: 'Category B', value: 200 },
            { label: 'Category C', value: 0 },
          ],
        },
      ],
    }

    render(<PieChart {...defaultProps} data={dataWithZeros} />)

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')

    expect(pieData).toHaveLength(3)
    expect(pieData[0].value).toBe(0)
    expect(pieData[1].value).toBe(200)
    expect(pieData[2].value).toBe(0)
  })

  it('cycles through colors when there are more data points than colors', () => {
    const manyDataPoints: ChartData = {
      datasets: [
        {
          label: 'Categories',
          data: [
            { label: 'A', value: 100 },
            { label: 'B', value: 200 },
            { label: 'C', value: 150 },
            { label: 'D', value: 50 },
            { label: 'E', value: 75 }, // Should cycle back to first color
          ],
        },
      ],
    }

    render(<PieChart {...defaultProps} data={manyDataPoints} />)

    const pieDataElement = screen.getByTestId('pie-data')
    const pieData = JSON.parse(pieDataElement.textContent || '[]')

    expect(pieData[0].color).toBe('#3F51B5') // First color
    expect(pieData[4].color).toBe('#3F51B5') // Cycles back to first color
  })
})

import React from 'react'
import {
  BarChart as RechartsBarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts'
import type { ChartData, ChartConfiguration, ChartDataPoint, LegendItem } from '@/types/dashboard'

interface BarChartProps {
  data: ChartData
  config: ChartConfiguration
  height: number
  responsive: boolean
  interactive: boolean
  exportable: boolean
  onDataClick?: (data: ChartDataPoint) => void
  onLegendClick?: (legendItem: LegendItem) => void
  colors: string[]
}

export function BarChart({
  data,
  config,
  height,
  responsive,
  interactive,
  onDataClick,
  onLegendClick,
  colors,
}: BarChartProps) {
  // Transform data for Recharts format
  const chartData = React.useMemo(() => {
    if (!data.datasets || data.datasets.length === 0) return []

    // Get all unique x values (categories)
    const allXValues = new Set<string | number>()
    data.datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        if (point.x !== undefined) allXValues.add(point.x)
      })
    })

    // Convert to array and sort
    const sortedXValues = Array.from(allXValues).sort((a, b) => {
      if (typeof a === 'number' && typeof b === 'number') return a - b
      return String(a).localeCompare(String(b))
    })

    // Create chart data points
    return sortedXValues.map(xValue => {
      const dataPoint: any = { x: xValue }

      data.datasets.forEach((dataset, index) => {
        const point = dataset.data.find(p => p.x === xValue)
        dataPoint[dataset.label] = point?.y || point?.value || 0
      })

      return dataPoint
    })
  }, [data])

  // Handle bar click
  const handleBarClick = (data: any, index: number, event?: React.MouseEvent) => {
    if (!interactive || !onDataClick) return

    // Find the clicked series from the event target or use first non-x key
    const clickedSeries = event?.currentTarget?.getAttribute('data-key') || 
                         Object.keys(data).find(key => key !== 'x') || ''

    const chartDataPoint: ChartDataPoint = {
      x: data.x,
      y: data[clickedSeries] || 0,
      value: data[clickedSeries] || 0,
      label: data.x?.toString(),
      metadata: {
        category: data.x,
        series: clickedSeries,
        index,
        chartType: 'bar',
        allData: data, // Include all data for advanced filtering
      },
    }
    onDataClick(chartDataPoint)
  }

  // Handle legend click
  const handleLegendClick = (data: any, index: number) => {
    if (!interactive || !onLegendClick) return

    const legendItem: LegendItem = {
      label: data.value,
      color: data.color,
      visible: true,
      metadata: {
        series: data.value,
        chartType: 'bar',
        index,
      },
    }
    onLegendClick(legendItem)
  }

  // Custom tooltip
  const CustomTooltip = ({ active, payload, label }: any) => {
    if (!active || !payload || !payload.length) return null

    return (
      <div className="bg-background border border-border rounded-lg shadow-lg p-3">
        <p className="text-sm font-medium text-foreground mb-2">{label}</p>
        {payload.map((entry: any, index: number) => (
          <div key={index} className="flex items-center gap-2 text-sm">
            <div 
              className="w-3 h-3 rounded-full" 
              style={{ backgroundColor: entry.color }}
            />
            <span className="text-muted-foreground">{entry.dataKey}:</span>
            <span className="font-medium text-foreground">
              {config.tooltip?.format ? config.tooltip.format(entry.value) : entry.value}
            </span>
          </div>
        ))}
      </div>
    )
  }

  const containerHeight = responsive ? height : height

  return (
    <ResponsiveContainer width="100%" height={containerHeight}>
      <RechartsBarChart 
        data={chartData} 
        margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
      >
        {config.axes?.x?.show && (
          <XAxis 
            dataKey="x" 
            axisLine={false}
            tickLine={false}
            tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
          />
        )}
        {config.axes?.y?.show && (
          <YAxis 
            axisLine={false}
            tickLine={false}
            tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
            tickFormatter={config.axes?.y?.format}
          />
        )}
        <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
        {config.tooltip?.show && <Tooltip content={<CustomTooltip />} />}
        {config.legend?.show && (
          <Legend 
            onClick={interactive ? handleLegendClick : undefined}
            wrapperStyle={{ 
              paddingTop: '20px',
              cursor: interactive ? 'pointer' : 'default'
            }}
          />
        )}
        {data.datasets.map((dataset, index) => (
          <Bar
            key={dataset.label}
            dataKey={dataset.label}
            fill={colors[index]}
            onClick={interactive ? (data, index, event) => handleBarClick(data, index, event) : undefined}
            style={{ cursor: interactive ? 'pointer' : 'default' }}
            animationDuration={config.animations?.enabled ? config.animations.duration : 0}
            radius={[4, 4, 0, 0]} // Rounded top corners
          />
        ))}
      </RechartsBarChart>
    </ResponsiveContainer>
  )
}

import React from 'react'
import { ResponsiveContainer } from 'recharts'
import type { ChartData, ChartConfiguration, ChartDataPoint, LegendItem } from '@/types/dashboard'

interface HeatmapChartProps {
  data: ChartData
  config: ChartConfiguration
  height: number
  responsive: boolean
  interactive: boolean
  exportable: boolean
  onDataClick?: (data: ChartDataPoint) => void
  onLegendClick?: (legendItem: LegendItem) => void
  colors: string[]
}

interface HeatmapCell {
  x: string | number
  y: string | number
  value: number
  color: string
  originalData: ChartDataPoint
}

export function HeatmapChart({
  data,
  config,
  height,
  responsive,
  interactive,
  onDataClick,
  onLegendClick,
  colors,
}: HeatmapChartProps) {
  // Transform data for heatmap format
  const { heatmapData, xLabels, yLabels, minValue, maxValue } = React.useMemo(() => {
    if (!data.datasets || data.datasets.length === 0) {
      return { heatmapData: [], xLabels: [], yLabels: [], minValue: 0, maxValue: 0 }
    }

    const allData: HeatmapCell[] = []
    const xSet = new Set<string | number>()
    const ySet = new Set<string | number>()
    let min = Infinity
    let max = -Infinity

    // Collect all data points
    data.datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        if (point.x !== undefined && point.y !== undefined) {
          const value = point.value || 0
          xSet.add(point.x)
          ySet.add(point.y)
          min = Math.min(min, value)
          max = Math.max(max, value)

          allData.push({
            x: point.x,
            y: point.y,
            value,
            color: point.color || '',
            originalData: point,
          })
        }
      })
    })

    const xLabels = Array.from(xSet).sort((a, b) => {
      if (typeof a === 'number' && typeof b === 'number') return a - b
      return String(a).localeCompare(String(b))
    })

    const yLabels = Array.from(ySet).sort((a, b) => {
      if (typeof a === 'number' && typeof b === 'number') return a - b
      return String(a).localeCompare(String(b))
    })

    return {
      heatmapData: allData,
      xLabels,
      yLabels,
      minValue: min === Infinity ? 0 : min,
      maxValue: max === -Infinity ? 0 : max,
    }
  }, [data])

  // Generate color based on value
  const getColorForValue = (value: number): string => {
    if (maxValue === minValue) return colors[0] || '#3F51B5'

    const normalizedValue = (value - minValue) / (maxValue - minValue)

    // Use a gradient from light to dark based on the primary color
    const baseColor = colors[0] || '#3F51B5'
    const opacity = 0.2 + (normalizedValue * 0.8) // Range from 0.2 to 1.0

    // Convert hex to rgba
    const hex = baseColor.replace('#', '')
    const r = parseInt(hex.substr(0, 2), 16)
    const g = parseInt(hex.substr(2, 2), 16)
    const b = parseInt(hex.substr(4, 2), 16)

    return `rgba(${r}, ${g}, ${b}, ${opacity})`
  }

  // Handle cell click
  const handleCellClick = (cell: HeatmapCell) => {
    if (!interactive || !onDataClick) return

    const chartDataPoint: ChartDataPoint = {
      x: cell.x,
      y: cell.y,
      value: cell.value,
      label: `${cell.x}, ${cell.y}`,
      metadata: cell.originalData.metadata,
    }
    onDataClick(chartDataPoint)
  }

  // Calculate cell dimensions
  const cellWidth = Math.max(40, (responsive ? 600 : 600) / Math.max(xLabels.length, 1))
  const cellHeight = Math.max(30, (height - 100) / Math.max(yLabels.length, 1))
  const chartWidth = cellWidth * xLabels.length + 100 // Add margin for labels
  const chartHeight = cellHeight * yLabels.length + 100 // Add margin for labels

  // Custom tooltip
  const [tooltip, setTooltip] = React.useState<{
    visible: boolean
    x: number
    y: number
    data: HeatmapCell | null
  }>({
    visible: false,
    x: 0,
    y: 0,
    data: null,
  })

  const handleMouseEnter = (event: React.MouseEvent, cell: HeatmapCell) => {
    if (!config.tooltip?.show) return

    setTooltip({
      visible: true,
      x: event.clientX,
      y: event.clientY,
      data: cell,
    })
  }

  const handleMouseLeave = () => {
    setTooltip(prev => ({ ...prev, visible: false }))
  }

  const containerHeight = responsive ? Math.max(height, chartHeight) : height

  return (
    <div className="relative w-full" style={{ height: containerHeight }}>
      <ResponsiveContainer width="100%" height={containerHeight}>
        <div className="flex flex-col items-center">
          {/* Chart Title */}
          {data.metadata?.title && (
            <h3 className="text-lg font-semibold text-foreground mb-4">
              {data.metadata.title}
            </h3>
          )}

          {/* Heatmap Grid */}
          <div className="relative">
            <svg
              width={chartWidth}
              height={chartHeight}
              className="overflow-visible"
            >
              {/* Y-axis labels */}
              {yLabels.map((yLabel, yIndex) => (
                <text
                  key={`y-${yIndex}`}
                  x={80}
                  y={90 + yIndex * cellHeight + cellHeight / 2}
                  textAnchor="end"
                  dominantBaseline="middle"
                  fontSize={12}
                  fill="hsl(var(--muted-foreground))"
                >
                  {yLabel}
                </text>
              ))}

              {/* X-axis labels */}
              {xLabels.map((xLabel, xIndex) => (
                <text
                  key={`x-${xIndex}`}
                  x={90 + xIndex * cellWidth + cellWidth / 2}
                  y={80}
                  textAnchor="middle"
                  dominantBaseline="middle"
                  fontSize={12}
                  fill="hsl(var(--muted-foreground))"
                  transform={`rotate(-45, ${90 + xIndex * cellWidth + cellWidth / 2}, 80)`}
                >
                  {xLabel}
                </text>
              ))}

              {/* Heatmap cells */}
              {yLabels.map((yLabel, yIndex) =>
                xLabels.map((xLabel, xIndex) => {
                  const cell = heatmapData.find(d => d.x === xLabel && d.y === yLabel)
                  const value = cell?.value || 0
                  const color = cell?.color || getColorForValue(value)

                  return (
                    <rect
                      key={`cell-${xIndex}-${yIndex}`}
                      x={90 + xIndex * cellWidth}
                      y={90 + yIndex * cellHeight}
                      width={cellWidth - 1}
                      height={cellHeight - 1}
                      fill={color}
                      stroke="hsl(var(--border))"
                      strokeWidth={0.5}
                      style={{ cursor: interactive ? 'pointer' : 'default' }}
                      onClick={() => cell && handleCellClick(cell)}
                      onMouseEnter={(e) => cell && handleMouseEnter(e, cell)}
                      onMouseLeave={handleMouseLeave}
                    />
                  )
                })
              )}
            </svg>
          </div>

          {/* Color Legend */}
          {config.legend?.show && (
            <div className="flex items-center gap-4 mt-4">
              <span className="text-sm text-muted-foreground">
                {config.tooltip?.format ? config.tooltip.format(minValue) : minValue}
              </span>
              <div className="flex">
                {Array.from({ length: 10 }).map((_, index) => {
                  const value = minValue + (index / 9) * (maxValue - minValue)
                  return (
                    <div
                      key={index}
                      className="w-4 h-4"
                      style={{ backgroundColor: getColorForValue(value) }}
                    />
                  )
                })}
              </div>
              <span className="text-sm text-muted-foreground">
                {config.tooltip?.format ? config.tooltip.format(maxValue) : maxValue}
              </span>
            </div>
          )}
        </div>
      </ResponsiveContainer>

      {/* Custom Tooltip */}
      {tooltip.visible && tooltip.data && (
        <div
          className="absolute z-50 bg-background border border-border rounded-lg shadow-lg p-3 pointer-events-none"
          style={{
            left: tooltip.x + 10,
            top: tooltip.y - 10,
          }}
        >
          <div className="text-sm">
            <div className="font-medium text-foreground">
              {tooltip.data.x} × {tooltip.data.y}
            </div>
            <div className="text-muted-foreground">
              Value: <span className="font-medium text-foreground">
                {config.tooltip?.format 
                  ? config.tooltip.format(tooltip.data.value) 
                  : tooltip.data.value
                }
              </span>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

import React from 'react'
import {
  LineChart as RechartsLineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Area,
  AreaChart,
  Scatter,
  ScatterChart,
} from 'recharts'
import type { ChartData, ChartConfiguration, ChartDataPoint, LegendItem } from '@/types/dashboard'

interface LineChartProps {
  data: ChartData
  config: ChartConfiguration
  height: number
  responsive: boolean
  interactive: boolean
  exportable: boolean
  onDataClick?: (data: ChartDataPoint) => void
  onLegendClick?: (legendItem: LegendItem) => void
  colors: string[]
  area?: boolean
  scatter?: boolean
}

export function LineChart({
  data,
  config,
  height,
  responsive,
  interactive,
  onDataClick,
  onLegendClick,
  colors,
  area = false,
  scatter = false,
}: LineChartProps) {
  // Transform data for Recharts format
  const chartData = React.useMemo(() => {
    if (!data.datasets || data.datasets.length === 0) return []

    // Get all unique x values
    const allXValues = new Set<string | number>()
    data.datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        if (point.x !== undefined) allXValues.add(point.x)
      })
    })

    // Convert to array and sort
    const sortedXValues = Array.from(allXValues).sort((a, b) => {
      if (typeof a === 'number' && typeof b === 'number') return a - b
      return String(a).localeCompare(String(b))
    })

    // Create chart data points
    return sortedXValues.map(xValue => {
      const dataPoint: any = { x: xValue }

      data.datasets.forEach((dataset, index) => {
        const point = dataset.data.find(p => p.x === xValue)
        dataPoint[dataset.label] = point?.y || point?.value || 0
      })

      return dataPoint
    })
  }, [data])

  // Handle data point click
  const handleDataClick = (data: any, index: number, event?: React.MouseEvent) => {
    if (!interactive || !onDataClick) return

    // Find the clicked series from the event target or use first non-x key
    const clickedSeries = event?.currentTarget?.getAttribute('data-key') || 
                         Object.keys(data).find(key => key !== 'x') || ''

    const chartDataPoint: ChartDataPoint = {
      x: data.x,
      y: data.value || data[clickedSeries] || 0,
      value: data.value || data[clickedSeries] || 0,
      label: data.x?.toString(),
      metadata: {
        category: data.x,
        series: clickedSeries,
        index,
        chartType: area ? 'area' : scatter ? 'scatter' : 'line',
        allData: data, // Include all data for advanced filtering
      },
    }
    onDataClick(chartDataPoint)
  }

  // Handle legend click
  const handleLegendClick = (data: any, index: number) => {
    if (!interactive || !onLegendClick) return

    const legendItem: LegendItem = {
      label: data.value,
      color: data.color,
      visible: true,
      metadata: {
        series: data.value,
        chartType: area ? 'area' : scatter ? 'scatter' : 'line',
        index,
      },
    }
    onLegendClick(legendItem)
  }

  // Custom tooltip
  const CustomTooltip = ({ active, payload, label }: any) => {
    if (!active || !payload || !payload.length) return null

    return (
      <div className="bg-background border border-border rounded-lg shadow-lg p-3">
        <p className="text-sm font-medium text-foreground mb-2">{label}</p>
        {payload.map((entry: any, index: number) => (
          <div key={index} className="flex items-center gap-2 text-sm">
            <div 
              className="w-3 h-3 rounded-full" 
              style={{ backgroundColor: entry.color }}
            />
            <span className="text-muted-foreground">{entry.dataKey}:</span>
            <span className="font-medium text-foreground">
              {config.tooltip?.format ? config.tooltip.format(entry.value) : entry.value}
            </span>
          </div>
        ))}
      </div>
    )
  }

  const containerHeight = responsive ? height : height

  if (scatter) {
    return (
      <ResponsiveContainer width="100%" height={containerHeight}>
        <ScatterChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          {config.axes?.x?.show && (
            <XAxis 
              dataKey="x" 
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
            />
          )}
          {config.axes?.y?.show && (
            <YAxis 
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
            />
          )}
          <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
          {config.tooltip?.show && <Tooltip content={<CustomTooltip />} />}
          {config.legend?.show && (
            <Legend 
              onClick={interactive ? handleLegendClick : undefined}
              wrapperStyle={{ 
                paddingTop: '20px',
                cursor: interactive ? 'pointer' : 'default'
              }}
            />
          )}
          {data.datasets.map((dataset, index) => (
            <Scatter
              key={dataset.label}
              dataKey={dataset.label}
              fill={colors[index]}
              onClick={interactive ? (data, index, event) => handleDataClick(data, index, event) : undefined}
              style={{ cursor: interactive ? 'pointer' : 'default' }}
            />
          ))}
        </ScatterChart>
      </ResponsiveContainer>
    )
  }

  if (area) {
    return (
      <ResponsiveContainer width="100%" height={containerHeight}>
        <AreaChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          {config.axes?.x?.show && (
            <XAxis 
              dataKey="x" 
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
            />
          )}
          {config.axes?.y?.show && (
            <YAxis 
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
            />
          )}
          <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
          {config.tooltip?.show && <Tooltip content={<CustomTooltip />} />}
          {config.legend?.show && (
            <Legend 
              onClick={interactive ? handleLegendClick : undefined}
              wrapperStyle={{ 
                paddingTop: '20px',
                cursor: interactive ? 'pointer' : 'default'
              }}
            />
          )}
          {data.datasets.map((dataset, index) => (
            <Area
              key={dataset.label}
              type="monotone"
              dataKey={dataset.label}
              stroke={colors[index]}
              fill={colors[index]}
              fillOpacity={0.3}
              strokeWidth={2}
              onClick={interactive ? (data, index, event) => handleDataClick(data, index, event) : undefined}
              style={{ cursor: interactive ? 'pointer' : 'default' }}
              animationDuration={config.animations?.enabled ? config.animations.duration : 0}
            />
          ))}
        </AreaChart>
      </ResponsiveContainer>
    )
  }

  return (
    <ResponsiveContainer width="100%" height={containerHeight}>
      <RechartsLineChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
        {config.axes?.x?.show && (
          <XAxis 
            dataKey="x" 
            axisLine={false}
            tickLine={false}
            tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
          />
        )}
        {config.axes?.y?.show && (
          <YAxis 
            axisLine={false}
            tickLine={false}
            tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
          />
        )}
        <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
        {config.tooltip?.show && <Tooltip content={<CustomTooltip />} />}
        {config.legend?.show && (
          <Legend 
            onClick={interactive ? handleLegendClick : undefined}
            wrapperStyle={{ 
              paddingTop: '20px',
              cursor: interactive ? 'pointer' : 'default'
            }}
          />
        )}
        {data.datasets.map((dataset, index) => (
          <Line
            key={dataset.label}
            type="monotone"
            dataKey={dataset.label}
            stroke={colors[index]}
            strokeWidth={2}
            dot={{ fill: colors[index], strokeWidth: 2, r: 4 }}
            activeDot={{ r: 6, stroke: colors[index], strokeWidth: 2 }}
            onClick={interactive ? (data, index, event) => handleDataClick(data, index, event) : undefined}
            style={{ cursor: interactive ? 'pointer' : 'default' }}
            animationDuration={config.animations?.enabled ? config.animations.duration : 0}
          />
        ))}
      </RechartsLineChart>
    </ResponsiveContainer>
  )
}

import React from 'react'
import {
  PieChart as RechartsPieChart,
  Pie,
  Cell,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts'
import type { ChartData, ChartConfiguration, ChartDataPoint, LegendItem } from '@/types/dashboard'

interface PieChartProps {
  data: ChartData
  config: ChartConfiguration
  height: number
  responsive: boolean
  interactive: boolean
  exportable: boolean
  onDataClick?: (data: ChartDataPoint) => void
  onLegendClick?: (legendItem: LegendItem) => void
  colors: string[]
}

export function PieChart({
  data,
  config,
  height,
  responsive,
  interactive,
  onDataClick,
  onLegendClick,
  colors,
}: PieChartProps) {
  // Transform data for Recharts pie chart format
  const chartData = React.useMemo(() => {
    if (!data.datasets || data.datasets.length === 0) return []

    // For pie charts, we typically use the first dataset
    const dataset = data.datasets[0]
    if (!dataset) return []

    return dataset.data.map((point, index) => ({
      name: point.label || point.x?.toString() || `Item ${index + 1}`,
      value: point.value || point.y || 0,
      color: point.color || colors[index % colors.length],
      originalData: point,
    }))
  }, [data, colors])

  // Handle pie slice click
  const handleSliceClick = (data: any, index: number, event?: React.MouseEvent) => {
    if (!interactive || !onDataClick) return

    const totalValue = chartData.reduce((sum, item) => sum + item.value, 0)
    const percentage = totalValue > 0 ? ((data.value / totalValue) * 100).toFixed(1) : '0'

    const chartDataPoint: ChartDataPoint = {
      x: data.name,
      y: data.value,
      value: data.value,
      label: data.name,
      color: data.color,
      metadata: {
        ...data.originalData?.metadata,
        category: data.name,
        percentage,
        index,
        chartType: 'pie',
        totalValue,
        allData: data, // Include all data for advanced filtering
      },
    }
    onDataClick(chartDataPoint)
  }

  // Handle legend click
  const handleLegendClick = (data: any, index: number) => {
    if (!interactive || !onLegendClick) return

    const legendItem: LegendItem = {
      label: data.value,
      color: data.color,
      visible: true,
      metadata: {
        category: data.value,
        chartType: 'pie',
        index,
      },
    }
    onLegendClick(legendItem)
  }

  // Custom tooltip
  const CustomTooltip = ({ active, payload }: any) => {
    if (!active || !payload || !payload.length) return null

    const data = payload[0]
    const percentage = ((data.value / chartData.reduce((sum, item) => sum + item.value, 0)) * 100).toFixed(1)

    return (
      <div className="bg-background border border-border rounded-lg shadow-lg p-3">
        <div className="flex items-center gap-2 text-sm">
          <div 
            className="w-3 h-3 rounded-full" 
            style={{ backgroundColor: data.payload.color }}
          />
          <span className="font-medium text-foreground">{data.name}</span>
        </div>
        <div className="mt-1 text-sm text-muted-foreground">
          Value: <span className="font-medium text-foreground">
            {config.tooltip?.format ? config.tooltip.format(data.value) : data.value}
          </span>
        </div>
        <div className="text-sm text-muted-foreground">
          Percentage: <span className="font-medium text-foreground">{percentage}%</span>
        </div>
      </div>
    )
  }

  // Custom label renderer
  const renderCustomLabel = ({ cx, cy, midAngle, innerRadius, outerRadius, percent }: any) => {
    if (percent < 0.05) return null // Don't show labels for slices smaller than 5%

    const RADIAN = Math.PI / 180
    const radius = innerRadius + (outerRadius - innerRadius) * 0.5
    const x = cx + radius * Math.cos(-midAngle * RADIAN)
    const y = cy + radius * Math.sin(-midAngle * RADIAN)

    return (
      <text 
        x={x} 
        y={y} 
        fill="white" 
        textAnchor={x > cx ? 'start' : 'end'} 
        dominantBaseline="central"
        fontSize={12}
        fontWeight="medium"
      >
        {`${(percent * 100).toFixed(0)}%`}
      </text>
    )
  }

  // Custom legend
  const CustomLegend = ({ payload }: any) => {
    if (!config.legend?.show) return null

    return (
      <div className="flex flex-wrap justify-center gap-4 mt-4">
        {payload.map((entry: any, index: number) => (
          <div
            key={index}
            className={`flex items-center gap-2 text-sm ${
              interactive ? 'cursor-pointer hover:opacity-80' : ''
            }`}
            onClick={() => handleLegendClick(entry, index)}
          >
            <div 
              className="w-3 h-3 rounded-full" 
              style={{ backgroundColor: entry.color }}
            />
            <span className="text-foreground">{entry.value}</span>
          </div>
        ))}
      </div>
    )
  }

  const containerHeight = responsive ? height : height

  return (
    <ResponsiveContainer width="100%" height={containerHeight}>
      <RechartsPieChart>
        <Pie
          data={chartData}
          cx="50%"
          cy="50%"
          labelLine={false}
          label={renderCustomLabel}
          outerRadius={Math.min(containerHeight * 0.35, 120)}
          fill="#8884d8"
          dataKey="value"
          onClick={interactive ? (data, index, event) => handleSliceClick(data, index, event) : undefined}
          style={{ cursor: interactive ? 'pointer' : 'default' }}
          animationDuration={config.animations?.enabled ? config.animations.duration : 0}
        >
          {chartData.map((entry, index) => (
            <Cell 
              key={`cell-${index}`} 
              fill={entry.color}
            />
          ))}
        </Pie>
        {config.tooltip?.show && <Tooltip content={<CustomTooltip />} />}
        {config.legend?.show && <Legend content={<CustomLegend />} />}
      </RechartsPieChart>
    </ResponsiveContainer>
  )
}

