interface FilterControlsProps {
  className?: string
  searchPlaceholder?: string
  showAdvancedFilters?: boolean
  customFilterOptions?: Array<{
    key: string
    label: string
    type: 'string' | 'number' | 'select'
    options?: string[]
  }>
}

export function FilterControls({
  className,
  searchPlaceholder = 'Search...',
  showAdvancedFilters = true,
  customFilterOptions = [],
}: FilterControlsProps) {
  const { filters, setFilter, removeFilter, clearFilters, isFiltered } = useFilters()
  const [searchValue, setSearchValue] = useState('')
  const [isAdvancedOpen, setIsAdvancedOpen] = useState(false)
  const [newFilterKey, setNewFilterKey] = useState('')
  const [newFilterValue, setNewFilterValue] = useState('')
  const [newFilterType, setNewFilterType] = useState<'string' | 'number' | 'boolean'>('string')
  const [newFilterOperator, setNewFilterOperator] = useState<'equals' | 'contains' | 'greaterThan' | 'lessThan'>('contains')

  // Handle search
  const handleSearchChange = (value: string) => {
    setSearchValue(value)
    if (value.trim()) {
      setFilter('search', {
        type: 'string',
        value: value.trim(),
        operator: 'contains',
      })
    } else {
      removeFilter('search')
    }
  }

  // Handle adding custom filter
  const handleAddCustomFilter = () => {
    if (!newFilterKey.trim() || !newFilterValue.trim()) return

    const filterValue: FilterValue = {
      type: newFilterType,
      value: newFilterType === 'number' ? Number(newFilterValue) : newFilterValue,
      operator: newFilterOperator,
    }

    setFilter(newFilterKey.trim(), filterValue)
    setNewFilterKey('')
    setNewFilterValue('')
    setIsAdvancedOpen(false)
  }

  // Handle predefined custom filter
  const handleCustomFilterChange = (key: string, value: string, type: 'string' | 'number' | 'select') => {
    if (value) {
      const filterValue: FilterValue = {
        type,
        value: type === 'number' ? Number(value) : value,
        operator: type === 'number' ? 'equals' : 'contains',
      }
      setFilter(key, filterValue)
    } else {
      removeFilter(key)
    }
  }

  return (
    <div className={cn('space-y-4', className)}>
      {/* Search Bar */}
      <div className="relative">
        <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
        <Input
          placeholder={searchPlaceholder}
          value={searchValue}
          onChange={(e) => handleSearchChange(e.target.value)}
          className="pl-9 pr-9"
        />
        {searchValue && (
          <Button
            variant="ghost"
            size="sm"
            onClick={() => handleSearchChange('')}
            className="absolute right-1 top-1/2 h-7 w-7 -translate-y-1/2 p-0"
          >
            <X className="h-4 w-4" />
          </Button>
        )}
      </div>

      {/* Quick Actions */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          {showAdvancedFilters && (
            <Popover open={isAdvancedOpen} onOpenChange={setIsAdvancedOpen}>
              <PopoverTrigger asChild>
                <Button variant="outline" size="sm" className="gap-2">
                  <SlidersHorizontal className="h-4 w-4" />
                  Advanced
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-80" align="start">
                <Card className="border-0 shadow-none">
                  <CardHeader className="pb-3">
                    <CardTitle className="text-base">Advanced Filters</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {/* Predefined Custom Filters */}
                    {customFilterOptions.map((option) => (
                      <div key={option.key} className="space-y-2">
                        <Label className="text-sm font-medium">{option.label}</Label>
                        {option.type === 'select' && option.options ? (
                          <Select
                            value={filters.customFilters?.[option.key]?.value || ''}
                            onValueChange={(value) =>
                              handleCustomFilterChange(option.key, value, option.type)
                            }
                          >
                            <SelectTrigger>
                              <SelectValue placeholder={`Select ${option.label.toLowerCase()}`} />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="">All</SelectItem>
                              {option.options.map((opt) => (
                                <SelectItem key={opt} value={opt}>
                                  {opt}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        ) : (
                          <Input
                            type={option.type === 'number' ? 'number' : 'text'}
                            placeholder={`Enter ${option.label.toLowerCase()}`}
                            value={filters.customFilters?.[option.key]?.value || ''}
                            onChange={(e) =>
                              handleCustomFilterChange(option.key, e.target.value, option.type)
                            }
                          />
                        )}
                      </div>
                    ))}

                    {/* Custom Filter Builder */}
                    <div className="space-y-3 border-t pt-4">
                      <Label className="text-sm font-medium">Add Custom Filter</Label>
                      <div className="grid grid-cols-2 gap-2">
                        <Input
                          placeholder="Filter name"
                          value={newFilterKey}
                          onChange={(e) => setNewFilterKey(e.target.value)}
                        />
                        <Select value={newFilterType} onValueChange={(value: any) => setNewFilterType(value)}>
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="string">Text</SelectItem>
                            <SelectItem value="number">Number</SelectItem>
                            <SelectItem value="boolean">Boolean</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                      <div className="grid grid-cols-2 gap-2">
                        <Input
                          placeholder="Filter value"
                          type={newFilterType === 'number' ? 'number' : 'text'}
                          value={newFilterValue}
                          onChange={(e) => setNewFilterValue(e.target.value)}
                        />
                        <Select value={newFilterOperator} onValueChange={(value: any) => setNewFilterOperator(value)}>
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="equals">Equals</SelectItem>
                            <SelectItem value="contains">Contains</SelectItem>
                            {newFilterType === 'number' && (
                              <>
                                <SelectItem value="greaterThan">Greater Than</SelectItem>
                                <SelectItem value="lessThan">Less Than</SelectItem>
                              </>
                            )}
                          </SelectContent>
                        </Select>
                      </div>
                      <Button
                        onClick={handleAddCustomFilter}
                        disabled={!newFilterKey.trim() || !newFilterValue.trim()}
                        className="w-full gap-2"
                        size="sm"
                      >
                        <Plus className="h-4 w-4" />
                        Add Filter
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              </PopoverContent>
            </Popover>
          )}
        </div>

        {isFiltered && (
          <Button
            variant="ghost"
            size="sm"
            onClick={clearFilters}
            className="gap-2 text-muted-foreground hover:text-foreground"
          >
            <X className="h-4 w-4" />
            Clear All
          </Button>
        )}
      </div>

      {/* Active Custom Filters */}
      {filters.customFilters && Object.keys(filters.customFilters).length > 0 && (
        <div className="space-y-2">
          <Label className="text-sm font-medium text-muted-foreground">Active Filters</Label>
          <div className="flex flex-wrap gap-2">
            {Object.entries(filters.customFilters).map(([key, filter]) => (
              <Badge key={key} variant="secondary" className="gap-1">
                {key}: {String(filter.value)}
                <X
                  className="h-3 w-3 cursor-pointer hover:text-destructive"
                  onClick={() => removeFilter(key)}
                />
              </Badge>
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

// Hook for filtering data based on current filters
export function useFilteredData<T extends Record<string, any>>(
  data: T[],
  searchFields: (keyof T)[] = []
): T[] {
  const { filters } = useFilters()

  return React.useMemo(() => {
    if (!data) return []

    return data.filter((item) => {
      // Search filter
      if (filters.customFilters?.search) {
        const searchValue = filters.customFilters.search.value.toLowerCase()
        const matchesSearch = searchFields.some((field) => {
          const fieldValue = String(item[field] || '').toLowerCase()
          return fieldValue.includes(searchValue)
        })
        if (!matchesSearch) return false
      }

      // Date range filter
      if (filters.dateRange && item.createdAt) {
        const itemDate = new Date(item.createdAt)
        if (itemDate < filters.dateRange.start || itemDate > filters.dateRange.end) {
          return false
        }
      }

      // Category filter
      if (filters.categories?.length && item.category) {
        if (!filters.categories.includes(item.category)) return false
      }

      // Status filter
      if (filters.status?.length && item.status) {
        if (!filters.status.includes(item.status)) return false
      }

      // Priority filter
      if (filters.priority?.length && item.priority) {
        if (!filters.priority.includes(item.priority)) return false
      }

      // Platform filter
      if (filters.platforms?.length && item.platform) {
        if (!filters.platforms.includes(item.platform)) return false
      }

      // Agent filter
      if (filters.agents?.length && item.agent) {
        if (!filters.agents.includes(item.agent)) return false
      }

      // Custom filters
      if (filters.customFilters) {
        for (const [key, filter] of Object.entries(filters.customFilters)) {
          if (key === 'search') continue // Already handled above

          const itemValue = item[key]
          if (itemValue === undefined || itemValue === null) continue

          switch (filter.operator) {
            case 'equals':
              if (itemValue !== filter.value) return false
              break
            case 'contains':
              if (!String(itemValue).toLowerCase().includes(String(filter.value).toLowerCase())) {
                return false
              }
              break
            case 'greaterThan':
              if (Number(itemValue) <= Number(filter.value)) return false
              break
            case 'lessThan':
              if (Number(itemValue) >= Number(filter.value)) return false
              break
          }
        }
      }

      return true
    })
  }, [data, filters, searchFields])
}

'use client'

import React, { useEffect, useCallback, useRef } from 'react'
import { useFilters } from './filter-context'
import type { FilterState, FilterValue } from '@/types/dashboard'

interface FilterSynchronizerProps {
  children: React.ReactNode
  // Synchronization options
  syncAcrossCards?: boolean
  syncAcrossCharts?: boolean
  syncDelay?: number // Debounce delay in ms
  onFilterChange?: (filters: FilterState) => void
  onFilterApplied?: (key: string, value: FilterValue) => void
  onFilterRemoved?: (key: string) => void
}

export function FilterSynchronizer({
  children,
  syncAcrossCards = true,
  syncAcrossCharts = true,
  syncDelay = 300,
  onFilterChange,
  onFilterApplied,
  onFilterRemoved,
}: FilterSynchronizerProps) {
  const { filters } = useFilters()
  const previousFiltersRef = useRef<FilterState>({})
  const timeoutRef = useRef<NodeJS.Timeout | undefined>(undefined)

  // Debounced filter change handler
  const debouncedFilterChange = useCallback(() => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current)
    }

    timeoutRef.current = setTimeout(() => {
      onFilterChange?.(filters)
    }, syncDelay)
  }, [filters, syncDelay, onFilterChange])

  // Track filter changes and call appropriate callbacks
  useEffect(() => {
    const previousFilters = previousFiltersRef.current
    const currentFilters = filters

    // Check for added/changed filters
    if (currentFilters.customFilters) {
      Object.entries(currentFilters.customFilters).forEach(([key, value]) => {
        const previousValue = previousFilters.customFilters?.[key]
        if (!previousValue || JSON.stringify(previousValue) !== JSON.stringify(value)) {
          onFilterApplied?.(key, value)
        }
      })
    }

    // Check for removed filters
    if (previousFilters.customFilters) {
      Object.keys(previousFilters.customFilters).forEach((key) => {
        if (!currentFilters.customFilters?.[key]) {
          onFilterRemoved?.(key)
        }
      })
    }

    // Check for other filter changes
    const filterKeys: (keyof FilterState)[] = [
      'dateRange',
      'categories',
      'status',
      'priority',
      'platforms',
      'agents',
    ]

    filterKeys.forEach((key) => {
      const currentValue = currentFilters[key]
      const previousValue = previousFilters[key]

      if (JSON.stringify(currentValue) !== JSON.stringify(previousValue)) {
        if (currentValue && key !== 'dateRange') {
          // For array filters, create a filter value
          const filterValue: FilterValue = {
            type: 'array',
            value: currentValue,
            operator: 'equals',
          }
          onFilterApplied?.(key, filterValue)
        } else if (currentValue && key === 'dateRange') {
          // For date range, create a special filter value
          const filterValue: FilterValue = {
            type: 'date',
            value: currentValue,
            operator: 'between',
          }
          onFilterApplied?.(key, filterValue)
        } else if (!currentValue && previousValue) {
          onFilterRemoved?.(key)
        }
      }
    })

    // Update previous filters reference
    previousFiltersRef.current = { ...currentFilters }

    // Call debounced change handler
    debouncedFilterChange()
  }, [filters, onFilterApplied, onFilterRemoved, debouncedFilterChange])

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
      }
    }
  }, [])

  return <>{children}</>
}

// Hook for components to react to filter changes
export function useFilterSync(
  callback: (filters: FilterState) => void,
  dependencies: React.DependencyList = []
) {
  const { filters } = useFilters()
  const callbackRef = useRef(callback)

  // Update callback ref when callback changes
  useEffect(() => {
    callbackRef.current = callback
  }, [callback])

  // Call callback when filters change
  useEffect(() => {
    callbackRef.current(filters)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [filters, ...dependencies])
}

// Hook for components to get filtered data that updates automatically
export function useSyncedFilteredData<T extends Record<string, any>>(
  data: T[],
  searchFields: (keyof T)[] = [],
  customFilterLogic?: (item: T, filters: FilterState) => boolean
): T[] {
  const { filters } = useFilters()

  return React.useMemo(() => {
    if (!data) return []

    return data.filter((item) => {
      // Custom filter logic takes precedence
      if (customFilterLogic) {
        return customFilterLogic(item, filters)
      }

      // Search filter
      if (filters.customFilters?.search) {
        const searchValue = filters.customFilters.search.value.toLowerCase()
        const matchesSearch = searchFields.some((field) => {
          const fieldValue = String(item[field] || '').toLowerCase()
          return fieldValue.includes(searchValue)
        })
        if (!matchesSearch) return false
      }

      // Date range filter
      if (filters.dateRange && item.createdAt) {
        const itemDate = new Date(item.createdAt)
        if (itemDate < filters.dateRange.start || itemDate > filters.dateRange.end) {
          return false
        }
      }

      // Array-based filters
      const arrayFilters: Array<{ key: keyof FilterState; itemKey: keyof T }> = [
        { key: 'categories', itemKey: 'category' },
        { key: 'status', itemKey: 'status' },
        { key: 'priority', itemKey: 'priority' },
        { key: 'platforms', itemKey: 'platform' },
        { key: 'agents', itemKey: 'agent' },
      ]

      for (const { key, itemKey } of arrayFilters) {
        const filterValues = filters[key] as string[] | undefined
        if (filterValues?.length && item[itemKey]) {
          if (!filterValues.includes(String(item[itemKey]))) return false
        }
      }

      // Custom filters
      if (filters.customFilters) {
        for (const [key, filter] of Object.entries(filters.customFilters)) {
          if (key === 'search') continue // Already handled above

          const itemValue = item[key as keyof T]
          if (itemValue === undefined || itemValue === null) continue

          switch (filter.operator) {
            case 'equals':
              if (itemValue !== filter.value) return false
              break
            case 'contains':
              if (!String(itemValue).toLowerCase().includes(String(filter.value).toLowerCase())) {
                return false
              }
              break
            case 'greaterThan':
              if (Number(itemValue) <= Number(filter.value)) return false
              break
            case 'lessThan':
              if (Number(itemValue) >= Number(filter.value)) return false
              break
            case 'between':
              if (Array.isArray(filter.value) && filter.value.length === 2) {
                const [min, max] = filter.value
                const numValue = Number(itemValue)
                if (numValue < Number(min) || numValue > Number(max)) return false
              }
              break
          }
        }
      }

      return true
    })
  }, [data, filters, searchFields, customFilterLogic])
}

// Hook for cross-card data filtering with enhanced capabilities
export function useCrossCardFiltering<T extends Record<string, any>>(
  data: T[],
  options: {
    searchFields?: (keyof T)[]
    filterMappings?: Record<string, (item: T, filterValue: FilterValue) => boolean>
    enabledFilters?: string[]
    customLogic?: (item: T, filters: FilterState) => boolean
  } = {}
): {
  filteredData: T[]
  activeFilters: Record<string, FilterValue>
  filterCount: number
} {
  const { filters } = useFilters()
  const {
    searchFields = [],
    filterMappings = {},
    enabledFilters,
    customLogic,
  } = options

  const result = React.useMemo(() => {
    if (!data) return { filteredData: [], activeFilters: {}, filterCount: 0 }

    const activeFilters = filters.customFilters || {}
    const filterCount = Object.keys(activeFilters).length

    // Filter data based on active filters
    const filteredData = data.filter(item => {
      // Custom logic takes precedence
      if (customLogic) {
        return customLogic(item, filters)
      }

      // Check each active filter
      return Object.entries(activeFilters).every(([filterKey, filterValue]) => {
        // Skip if this filter is not enabled
        if (enabledFilters && !enabledFilters.includes(filterKey)) {
          return true
        }

        // Use custom mapping if provided
        if (filterMappings[filterKey]) {
          return filterMappings[filterKey](item, filterValue)
        }

        // Default filtering logic
        const itemValue = item[filterKey as keyof T]

        if (itemValue === undefined || itemValue === null) return true

        switch (filterValue.operator) {
          case 'equals':
            return itemValue === filterValue.value
          case 'contains':
            return String(itemValue).toLowerCase().includes(String(filterValue.value).toLowerCase())
          case 'greaterThan':
            return Number(itemValue) > Number(filterValue.value)
          case 'lessThan':
            return Number(itemValue) < Number(filterValue.value)
          case 'between':
            if (Array.isArray(filterValue.value) && filterValue.value.length === 2) {
              const [min, max] = filterValue.value
              return Number(itemValue) >= Number(min) && Number(itemValue) <= Number(max)
            }
            return true
          default:
            return itemValue === filterValue.value
        }
      })
    })

    return { filteredData, activeFilters, filterCount }
  }, [data, filters.customFilters, filterMappings, enabledFilters, customLogic])

  return result
}

// Component for visual filter state indicators
export function FilterStateIndicator({
  filterKey,
  className,
}: {
  filterKey: string
  className?: string
}) {
  const { filters, removeFilter } = useFilters()
  const filterValue = filters.customFilters?.[filterKey]

  if (!filterValue) return null

  return (
    <div className={`inline-flex items-center gap-1 px-2 py-1 bg-primary/10 text-primary text-xs rounded-full ${className}`}>
      <span>{filterKey}: {String(filterValue.value)}</span>
      <button
        onClick={() => removeFilter(filterKey)}
        className="hover:bg-primary/20 rounded-full p-0.5"
        aria-label={`Remove ${filterKey} filter`}
      >
        ×
      </button>
    </div>
  )
}

// Component for displaying all active filters
export function ActiveFiltersDisplay({
  className,
  showClearAll = true,
}: {
  className?: string
  showClearAll?: boolean
}) {
  const { filters, clearFilters } = useFilters()
  const activeFilters = filters.customFilters || {}

  if (Object.keys(activeFilters).length === 0) return null

  return (
    <div className={`flex flex-wrap items-center gap-2 ${className}`}>
      <span className="text-sm text-muted-foreground">Active filters:</span>
      {Object.keys(activeFilters).map(key => (
        <FilterStateIndicator key={key} filterKey={key} />
      ))}
      {showClearAll && (
        <button
          onClick={clearFilters}
          className="text-xs text-muted-foreground hover:text-foreground underline"
        >
          Clear all
        </button>
      )}
    </div>
  )
}

// Component for debugging filter state
export function FilterDebugger({ enabled = false }: { enabled?: boolean }) {
  const { filters, isFiltered, activeFilterCount } = useFilters()

  if (!enabled) return null

  return (
    <div className="fixed bottom-4 right-4 bg-background border border-border rounded-lg p-4 shadow-lg max-w-sm z-50">
      <h3 className="font-semibold text-sm mb-2">Filter Debug</h3>
      <div className="space-y-1 text-xs">
        <div>Active: {isFiltered ? 'Yes' : 'No'}</div>
        <div>Count: {activeFilterCount}</div>
        <details className="mt-2">
          <summary className="cursor-pointer font-medium">Filter State</summary>
          <pre className="mt-1 text-xs bg-muted p-2 rounded overflow-auto max-h-40">
            {JSON.stringify(filters, null, 2)}
          </pre>
        </details>
      </div>
    </div>
  )
}

'use client'

import React, { useCallback } from 'react'
import { DashboardCard } from '../cards/dashboard-card'
import { useFilters } from './filter-context'
import type { DashboardCardProps, FilterValue } from '@/types/dashboard'

interface FilterableCardProps extends DashboardCardProps {
  // Filtering props
  enableFiltering?: boolean
  filterMappings?: {
    cardClick?: () => Record<string, FilterValue>
    metricClick?: () => Record<string, FilterValue>
  }
  filterData?: Record<string, any> // Data to use for filtering
}

export function FilterableCard({
  enableFiltering = true,
  filterMappings,
  filterData,
  onClick,
  onFilter,
  ...cardProps
}: FilterableCardProps) {
  const { setFilter, removeFilter, filters } = useFilters()

  // Handle card clicks with filtering
  const handleCardClick = useCallback(() => {
    // Call custom handler first
    onClick?.()

    if (!enableFiltering) return

    // Apply default or custom filter mapping
    let filtersToApply: Record<string, FilterValue> = {}

    if (filterMappings?.cardClick) {
      filtersToApply = filterMappings.cardClick()
    } else if (filterData) {
      // Default mapping: use filterData to create filters
      Object.entries(filterData).forEach(([key, value]) => {
        if (value !== null && value !== undefined) {
          filtersToApply[key] = {
            type: typeof value === 'number' ? 'number' : 'string',
            value,
            operator: 'equals',
          }
        }
      })
    } else if (cardProps.title) {
      // Fallback: filter by card title
      filtersToApply.cardTitle = {
        type: 'string',
        value: cardProps.title,
        operator: 'equals',
      }
    }

    // Apply filters
    Object.entries(filtersToApply).forEach(([key, filterValue]) => {
      // Toggle filter if it's already active with the same value
      const existingFilter = filters.customFilters?.[key]
      if (existingFilter && existingFilter.value === filterValue.value) {
        removeFilter(key)
      } else {
        setFilter(key, filterValue)
      }
    })

    // Call the onFilter callback if provided
    if (Object.keys(filtersToApply).length > 0) {
      const firstFilter = Object.values(filtersToApply)[0]
      onFilter?.(firstFilter)
    }
  }, [
    enableFiltering,
    filterMappings,
    filterData,
    cardProps.title,
    onClick,
    onFilter,
    setFilter,
    removeFilter,
    filters,
  ])

  return (
    <DashboardCard
      {...cardProps}
      interactive={cardProps.interactive || enableFiltering}
      onClick={handleCardClick}
      onFilter={onFilter}
    />
  )
}

// Higher-order component for adding filtering to existing cards
export function withCardFiltering<T extends DashboardCardProps>(
  WrappedComponent: React.ComponentType<T>,
  defaultFilterMappings?: FilterableCardProps['filterMappings']
) {
  return function FilterableCardComponent(props: T & Partial<FilterableCardProps>) {
    const {
      enableFiltering = true,
      filterMappings = defaultFilterMappings,
      filterData,
      onClick,
      onFilter,
      ...wrappedProps
    } = props

    const { setFilter, removeFilter, filters } = useFilters()

    const handleCardClick = useCallback(() => {
      onClick?.()

      if (!enableFiltering) return

      let filtersToApply: Record<string, FilterValue> = {}

      if (filterMappings?.cardClick) {
        filtersToApply = filterMappings.cardClick()
      } else if (filterData) {
        Object.entries(filterData).forEach(([key, value]) => {
          if (value !== null && value !== undefined) {
            filtersToApply[key] = {
              type: typeof value === 'number' ? 'number' : 'string',
              value,
              operator: 'equals',
            }
          }
        })
      }

      Object.entries(filtersToApply).forEach(([key, filterValue]) => {
        const existingFilter = filters.customFilters?.[key]
        if (existingFilter && existingFilter.value === filterValue.value) {
          removeFilter(key)
        } else {
          setFilter(key, filterValue)
        }
      })

      if (Object.keys(filtersToApply).length > 0) {
        const firstFilter = Object.values(filtersToApply)[0]
        onFilter?.(firstFilter)
      }
    }, [
      enableFiltering,
      filterMappings,
      filterData,
      onClick,
      onFilter,
      setFilter,
      removeFilter,
      filters,
    ])

    return (
      <WrappedComponent
        {...(wrappedProps as T)}
        interactive={wrappedProps.interactive || enableFiltering}
        onClick={handleCardClick}
        onFilter={onFilter}
      />
    )
  }
}

import React from 'react'
import { cn } from '@/lib/utils'
import { useIsMobile } from '../mobile/touch-interactions'
import type { DashboardGridProps } from '@/types/dashboard'

export function DashboardGrid({
  children,
  columns = 12,
  gap,
  className,
  responsive = {
    mobile: 1,
    tablet: 6,
    desktop: 12,
  },
  autoFlow = 'row',
  alignItems = 'stretch',
  justifyItems = 'stretch',
}: DashboardGridProps) {
  const isMobile = useIsMobile()

  // Use CSS custom properties for responsive behavior
  const gridStyle = {
    '--dashboard-grid-columns-mobile': responsive.mobile.toString(),
    '--dashboard-grid-columns-tablet': responsive.tablet.toString(),
    '--dashboard-grid-columns-desktop': responsive.desktop.toString(),
    '--dashboard-grid-gap': gap ? `${gap}px` : 'var(--dashboard-grid-gap)',
    gridAutoFlow: autoFlow,
    alignItems,
    justifyItems,
  } as React.CSSProperties

  return (
    <div
      className={cn(
        'dashboard-grid',
        'grid',
        // Responsive grid columns using CSS custom properties
        '[grid-template-columns:repeat(var(--dashboard-grid-columns-mobile),1fr)]',
        'md:[grid-template-columns:repeat(var(--dashboard-grid-columns-tablet),1fr)]',
        'lg:[grid-template-columns:repeat(var(--dashboard-grid-columns-desktop),1fr)]',
        // Mobile-specific optimizations
        isMobile && [
          'px-4', // Add horizontal padding on mobile
          'gap-4', // Reduce gap on mobile
          'pb-20', // Add bottom padding for mobile navigation
        ],
        className
      )}
      style={gridStyle}
    >
      {children}
    </div>
  )
}

