// Performance monitoring utilities
export interface PerformanceMetrics {
  renderTime: number
  chartLoadTime: number
  dataFetchTime: number
  interactionLatency: number
  memoryUsage: number
  bundleSize: number
}

export interface PerformanceThresholds {
  renderTime: number
  chartLoadTime: number
  dataFetchTime: number
  interactionLatency: number
}

// Performance monitoring hook
export function usePerformanceMonitoring() {
  const [metrics, setMetrics] = React.useState<PerformanceMetrics | null>(null)

  const startMeasurement = React.useCallback((name: string) => {
    if (typeof window !== 'undefined' && 'performance' in window) {
      performance.mark(`${name}-start`)
    }
  }, [])

  const endMeasurement = React.useCallback((name: string) => {
    if (typeof window !== 'undefined' && 'performance' in window) {
      performance.mark(`${name}-end`)
      performance.measure(name, `${name}-start`, `${name}-end`)

      const measure = performance.getEntriesByName(name)[0]
      if (measure) {
        console.log(`Performance: ${name} took ${measure.duration.toFixed(2)}ms`)

        // Send to analytics if available
        if ('gtag' in window) {
          (window as any).gtag('event', 'performance_measurement', {
            measurement_name: name,
            duration: Math.round(measure.duration),
          })
        }
      }
    }
  }, [])

  const measureComponent = React.useCallback((componentName: string) => {
    return {
      start: () => startMeasurement(componentName),
      end: () => endMeasurement(componentName),
    }
  }, [startMeasurement, endMeasurement])

  return {
    metrics,
    startMeasurement,
    endMeasurement,
    measureComponent,
  }
}

// Bundle size monitoring
export function getBundleSize(): Promise<number> {
  if (typeof window === 'undefined' || !('performance' in window)) {
    return Promise.resolve(0)
  }

  return new Promise((resolve) => {
    // Use Navigation Timing API to estimate bundle size
    const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming
    if (navigation) {
      const transferSize = navigation.transferSize || 0
      resolve(transferSize)
    } else {
      resolve(0)
    }
  })
}

// Memory usage monitoring
export function getMemoryUsage(): number {
  if (typeof window === 'undefined' || !('performance' in window)) {
    return 0
  }

  const memory = (performance as any).memory
  if (memory) {
    return memory.usedJSHeapSize || 0
  }

  return 0
}

// Core Web Vitals monitoring
export function trackCoreWebVitals() {
  if (typeof window === 'undefined') return

  // Track Largest Contentful Paint (LCP)
  const observer = new PerformanceObserver((list) => {
    const entries = list.getEntries()
    const lastEntry = entries[entries.length - 1]

    if ('gtag' in window) {
      (window as any).gtag('event', 'web_vitals', {
        metric_name: 'LCP',
        value: Math.round(lastEntry.startTime),
      })
    }
  })

  try {
    observer.observe({ entryTypes: ['largest-contentful-paint'] })
  } catch (e) {
    // LCP not supported
  }

  // Track First Input Delay (FID)
  const fidObserver = new PerformanceObserver((list) => {
    const entries = list.getEntries()
    entries.forEach((entry) => {
      const fid = entry.processingStart - entry.startTime

      if ('gtag' in window) {
        (window as any).gtag('event', 'web_vitals', {
          metric_name: 'FID',
          value: Math.round(fid),
        })
      }
    })
  })

  try {
    fidObserver.observe({ entryTypes: ['first-input'] })
  } catch (e) {
    // FID not supported
  }

  // Track Cumulative Layout Shift (CLS)
  let clsValue = 0
  const clsObserver = new PerformanceObserver((list) => {
    const entries = list.getEntries()
    entries.forEach((entry: any) => {
      if (!entry.hadRecentInput) {
        clsValue += entry.value
      }
    })

    if ('gtag' in window) {
      (window as any).gtag('event', 'web_vitals', {
        metric_name: 'CLS',
        value: Math.round(clsValue * 1000) / 1000,
      })
    }
  })

  try {
    clsObserver.observe({ entryTypes: ['layout-shift'] })
  } catch (e) {
    // CLS not supported
  }
}

// Performance budget checker
export function checkPerformanceBudget(thresholds: PerformanceThresholds): boolean {
  if (typeof window === 'undefined' || !('performance' in window)) {
    return true // Assume passing if we can't measure
  }

  const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming
  if (!navigation) return true

  const loadTime = navigation.loadEventEnd - navigation.navigationStart
  const renderTime = navigation.domContentLoadedEventEnd - navigation.navigationStart

  const withinBudget = {
    renderTime: renderTime <= thresholds.renderTime,
    loadTime: loadTime <= thresholds.chartLoadTime,
  }

  const allWithinBudget = Object.values(withinBudget).every(Boolean)

  if (!allWithinBudget) {
    console.warn('Performance budget exceeded:', {
      actual: { renderTime, loadTime },
      thresholds,
      withinBudget,
    })
  }

  return allWithinBudget
}

// Default performance thresholds (in milliseconds)
export const DEFAULT_PERFORMANCE_THRESHOLDS: PerformanceThresholds = {
  renderTime: 1000,      // 1 second for initial render
  chartLoadTime: 2000,   // 2 seconds for chart loading
  dataFetchTime: 3000,   // 3 seconds for data fetching
  interactionLatency: 100, // 100ms for interactions
}

import React from 'react'

import { useEffect, useRef, useState, useCallback } from 'react'

interface IntersectionObserverOptions {
  root?: Element | null
  rootMargin?: string
  threshold?: number | number[]
  triggerOnce?: boolean
  skip?: boolean
  initialInView?: boolean
  delay?: number
  trackVisibility?: boolean
  fallbackInView?: boolean
}

interface IntersectionObserverEntry {
  boundingClientRect: DOMRectReadOnly
  intersectionRatio: number
  intersectionRect: DOMRectReadOnly
  isIntersecting: boolean
  rootBounds: DOMRectReadOnly | null
  target: Element
  time: number
}

interface UseIntersectionObserverResult {
  ref: (node?: Element | null) => void
  inView: boolean
  entry?: IntersectionObserverEntry
}

// Polyfill for environments without IntersectionObserver
const hasIntersectionObserver = typeof window !== 'undefined' && 'IntersectionObserver' in window

function createIntersectionObserver(
  callback: (entries: IntersectionObserverEntry[]) => void,
  options: IntersectionObserverOptions = {}
): IntersectionObserver | null {
  if (!hasIntersectionObserver) {
    return null
  }

  return new IntersectionObserver(callback, {
    root: options.root,
    rootMargin: options.rootMargin || '0px',
    threshold: options.threshold || 0,
  })
}

// Hook for using intersection observer
export function useIntersectionObserver(
  options: IntersectionObserverOptions = {}
): UseIntersectionObserverResult {
  const {
    threshold = 0,
    root = null,
    rootMargin = '0px',
    triggerOnce = false,
    skip = false,
    initialInView = false,
    delay = 0,
    trackVisibility = false,
    fallbackInView = false,
  } = options

  const [inView, setInView] = useState(initialInView)
  const [entry, setEntry] = useState<IntersectionObserverEntry>()
  const elementRef = useRef<Element | null>(null)
  const observerRef = useRef<IntersectionObserver | null>(null)
  const timeoutRef = useRef<NodeJS.Timeout>()

  const setRef = useCallback(
    (node: Element | null) => {
      if (elementRef.current) {
        // Clean up previous observer
        if (observerRef.current) {
          observerRef.current.unobserve(elementRef.current)
        }
        if (timeoutRef.current) {
          clearTimeout(timeoutRef.current)
        }
      }

      elementRef.current = node

      if (skip || !node) return

      // Fallback for environments without IntersectionObserver
      if (!hasIntersectionObserver) {
        if (!triggerOnce || !inView) {
          setInView(fallbackInView)
        }
        return
      }

      observerRef.current = createIntersectionObserver(
        (entries) => {
          const [observerEntry] = entries
          const isIntersecting = observerEntry.isIntersecting

          // Handle visibility tracking
          if (trackVisibility && 'isVisible' in observerEntry) {
            const isVisible = (observerEntry as any).isVisible
            if (!isVisible) return
          }

          const handleIntersection = () => {
            setInView(isIntersecting)
            setEntry(observerEntry)

            if (isIntersecting && triggerOnce && observerRef.current) {
              observerRef.current.unobserve(node)
              observerRef.current = null
            }
          }

          if (delay > 0 && isIntersecting) {
            timeoutRef.current = setTimeout(handleIntersection, delay)
          } else {
            handleIntersection()
          }
        },
        {
          threshold,
          root,
          rootMargin,
          // Enable visibility tracking if supported
          ...(trackVisibility && { trackVisibility: true, delay: 100 }),
        }
      )

      if (observerRef.current && node) {
        observerRef.current.observe(node)
      }
    },
    [
      threshold,
      root,
      rootMargin,
      triggerOnce,
      skip,
      delay,
      trackVisibility,
      fallbackInView,
      inView,
    ]
  )

  useEffect(() => {
    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect()
      }
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
      }
    }
  }, [])

  return {
    ref: setRef,
    inView,
    entry,
  }
}

// Higher-order component for intersection observer
export function withIntersectionObserver<T extends object>(
  Component: React.ComponentType<T>,
  options: IntersectionObserverOptions = {}
) {
  const WrappedComponent = React.forwardRef<any, T & { onIntersect?: (inView: boolean) => void }>((props, ref) => {
    const { onIntersect, ...componentProps } = props
    const { ref: intersectionRef, inView } = useIntersectionObserver(options)

    useEffect(() => {
      onIntersect?.(inView)
    }, [inView, onIntersect])

    return (
      <div ref={intersectionRef}>
        <Component {...(componentProps as T)} ref={ref} />
      </div>
    )
  })

  WrappedComponent.displayName = `WithIntersectionObserver(${Component.displayName || Component.name})`

  return WrappedComponent
}

// Utility for batch intersection observer
export class BatchIntersectionObserver {
  private observer: IntersectionObserver | null = null
  private callbacks = new Map<Element, (entry: IntersectionObserverEntry) => void>()

  constructor(private options: IntersectionObserverOptions = {}) {
    if (hasIntersectionObserver) {
      this.observer = createIntersectionObserver(
        (entries) => {
          entries.forEach((entry) => {
            const callback = this.callbacks.get(entry.target)
            if (callback) {
              callback(entry)
            }
          })
        },
        options
      )
    }
  }

  observe(element: Element, callback: (entry: IntersectionObserverEntry) => void) {
    if (!this.observer) {
      // Fallback behavior
      callback({
        isIntersecting: this.options.fallbackInView || false,
        target: element,
      } as IntersectionObserverEntry)
      return
    }

    this.callbacks.set(element, callback)
    this.observer.observe(element)
  }

  unobserve(element: Element) {
    if (this.observer) {
      this.observer.unobserve(element)
    }
    this.callbacks.delete(element)
  }

  disconnect() {
    if (this.observer) {
      this.observer.disconnect()
    }
    this.callbacks.clear()
  }
}

// Singleton batch observer for performance optimization
let globalBatchObserver: BatchIntersectionObserver | null = null

export function getGlobalBatchObserver(options: IntersectionObserverOptions = {}) {
  if (!globalBatchObserver) {
    globalBatchObserver = new BatchIntersectionObserver(options)
  }
  return globalBatchObserver
}

// Hook for using the global batch observer
export function useBatchIntersectionObserver(
  callback: (entry: IntersectionObserverEntry) => void,
  options: IntersectionObserverOptions = {}
) {
  const elementRef = useRef<Element | null>(null)
  const batchObserver = getGlobalBatchObserver(options)

  const setRef = useCallback(
    (node: Element | null) => {
      if (elementRef.current) {
        batchObserver.unobserve(elementRef.current)
      }

      elementRef.current = node

      if (node) {
        batchObserver.observe(node, callback)
      }
    },
    [batchObserver, callback]
  )

  useEffect(() => {
    return () => {
      if (elementRef.current) {
        batchObserver.unobserve(elementRef.current)
      }
    }
  }, [batchObserver])

  return { ref: setRef }
}

// Performance monitoring for intersection observer
export function trackIntersectionPerformance(elementId: string) {
  if (typeof window !== 'undefined' && 'performance' in window) {
    const startTime = performance.now()

    return {
      end: () => {
        const endTime = performance.now()
        const observationTime = endTime - startTime

        // Log performance metrics
        console.log(`Intersection observation ${elementId}: ${observationTime.toFixed(2)}ms`)

        // Send to analytics if available
        if ('gtag' in window) {
          (window as any).gtag('event', 'intersection_performance', {
            element_id: elementId,
            observation_time: Math.round(observationTime),
          })
        }
      }
    }
  }

  return { end: () => {} }
}

// Utility for creating viewport-based loading priorities
export function getViewportBasedPriority(element: Element): 'critical' | 'high' | 'medium' | 'low' {
  if (typeof window === 'undefined') return 'medium'

  const rect = element.getBoundingClientRect()
  const viewportHeight = window.innerHeight
  const viewportWidth = window.innerWidth

  // Critical: Above the fold and in center of viewport
  if (
    rect.top >= 0 && 
    rect.bottom <= viewportHeight &&
    rect.left >= viewportWidth * 0.25 &&
    rect.right <= viewportWidth * 0.75
  ) {
    return 'critical'
  }

  // High: Above the fold
  if (rect.top >= 0 && rect.top <= viewportHeight) {
    return 'high'
  }

  // Medium: Within 2x viewport height
  if (rect.top <= viewportHeight * 2) {
    return 'medium'
  }

  // Low: Beyond 2x viewport height
  return 'low'
}

// Utility for preloading elements based on scroll direction
export function useScrollDirectionOptimization() {
  const [scrollDirection, setScrollDirection] = useState<'up' | 'down' | null>(null)
  const lastScrollY = useRef(0)

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY

      if (currentScrollY > lastScrollY.current) {
        setScrollDirection('down')
      } else if (currentScrollY < lastScrollY.current) {
        setScrollDirection('up')
      }

      lastScrollY.current = currentScrollY
    }

    window.addEventListener('scroll', handleScroll, { passive: true })

    return () => {
      window.removeEventListener('scroll', handleScroll)
    }
  }, [])

  return scrollDirection
}

import React, { useState, useRef, useEffect, Suspense } from 'react'
import { cn } from '@/lib/utils'
import { Skeleton } from '@/components/ui/skeleton'
import { Card, CardContent, CardHeader } from '@/components/ui/card'

interface LazyCardProps {
  children: React.ReactNode
  className?: string
  fallback?: React.ReactNode
  rootMargin?: string
  threshold?: number
  minHeight?: number
  onIntersect?: () => void
  onLoad?: () => void
  priority?: 'low' | 'medium' | 'high' | 'critical'
}

interface LazyCardSkeletonProps {
  minHeight?: number
  className?: string
}

function LazyCardSkeleton({ minHeight = 200, className }: LazyCardSkeletonProps) {
  return (
    <Card 
      className={cn('dashboard-card-skeleton', className)}
      style={{ minHeight: `${minHeight}px` }}
      role="status"
      aria-label="Loading card content"
    >
      <CardHeader className="space-y-2">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Skeleton className="h-5 w-5 rounded" />
            <div className="space-y-2">
              <Skeleton className="h-4 w-24" />
              <Skeleton className="h-3 w-16" />
            </div>
          </div>
          <Skeleton className="h-8 w-8 rounded" />
        </div>
      </CardHeader>
      <CardContent className="space-y-3">
        <div className="flex items-baseline space-x-2">
          <Skeleton className="h-8 w-16" />
          <Skeleton className="h-4 w-8" />
          <Skeleton className="h-6 w-12 rounded-full" />
        </div>
        <Skeleton className="h-20 w-full rounded" />
      </CardContent>
    </Card>
  )
}

export function LazyCard({
  children,
  className,
  fallback,
  rootMargin = '50px',
  threshold = 0.1,
  minHeight = 200,
  onIntersect,
  onLoad,
  priority = 'medium',
}: LazyCardProps) {
  const [isIntersecting, setIsIntersecting] = useState(false)
  const [hasLoaded, setHasLoaded] = useState(false)
  const cardRef = useRef<HTMLDivElement>(null)
  const observerRef = useRef<IntersectionObserver | null>(null)

  // Priority-based loading delay
  const getLoadingDelay = (priority: string) => {
    switch (priority) {
      case 'critical':
        return 0
      case 'high':
        return 50
      case 'medium':
        return 100
      case 'low':
        return 200
      default:
        return 100
    }
  }

  useEffect(() => {
    const currentRef = cardRef.current
    if (!currentRef) return

    // Create intersection observer
    observerRef.current = new IntersectionObserver(
      (entries) => {
        const [entry] = entries
        if (entry.isIntersecting && !isIntersecting) {
          // Add priority-based delay
          const delay = getLoadingDelay(priority)

          setTimeout(() => {
            setIsIntersecting(true)
            onIntersect?.()

            // Disconnect observer after first intersection
            if (observerRef.current) {
              observerRef.current.disconnect()
            }
          }, delay)
        }
      },
      {
        rootMargin,
        threshold,
      }
    )

    observerRef.current.observe(currentRef)

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect()
      }
    }
  }, [isIntersecting, rootMargin, threshold, priority, onIntersect])

  // Handle load completion
  useEffect(() => {
    if (isIntersecting && !hasLoaded) {
      // Small delay to ensure smooth loading
      const timer = setTimeout(() => {
        setHasLoaded(true)
        onLoad?.()
      }, 50)

      return () => clearTimeout(timer)
    }
  }, [isIntersecting, hasLoaded, onLoad])

  const defaultFallback = fallback || (
    <LazyCardSkeleton 
      minHeight={minHeight} 
      className={className}
    />
  )

  return (
    <div
      ref={cardRef}
      className={cn(
        'lazy-card-container',
        // Priority-based styling
        priority === 'critical' && 'lazy-card-critical',
        priority === 'high' && 'lazy-card-high',
        priority === 'medium' && 'lazy-card-medium',
        priority === 'low' && 'lazy-card-low',
        className
      )}
      style={{ minHeight: isIntersecting ? 'auto' : `${minHeight}px` }}
    >
      {isIntersecting ? (
        <Suspense fallback={defaultFallback}>
          <div className="lazy-card-content">
            {children}
          </div>
        </Suspense>
      ) : (
        defaultFallback
      )}
    </div>
  )
}

// Higher-order component for wrapping existing cards with lazy loading
export function withLazyLoading<T extends object>(
  Component: React.ComponentType<T>,
  options?: {
    rootMargin?: string
    threshold?: number
    minHeight?: number
    priority?: 'low' | 'medium' | 'high' | 'critical'
  }
) {
  const LazyWrappedComponent = React.forwardRef<any, T>((props, ref) => {
    return (
      <LazyCard
        rootMargin={options?.rootMargin}
        threshold={options?.threshold}
        minHeight={options?.minHeight}
        priority={options?.priority}
      >
        <Component {...props} ref={ref} />
      </LazyCard>
    )
  })

  LazyWrappedComponent.displayName = `LazyLoaded(${Component.displayName || Component.name})`

  return LazyWrappedComponent
}

// Hook for manual lazy loading control
export function useLazyLoading(
  options: {
    rootMargin?: string
    threshold?: number
    priority?: 'low' | 'medium' | 'high' | 'critical'
  } = {}
) {
  const [isIntersecting, setIsIntersecting] = useState(false)
  const [hasLoaded, setHasLoaded] = useState(false)
  const elementRef = useRef<HTMLElement>(null)

  useEffect(() => {
    const currentRef = elementRef.current
    if (!currentRef) return

    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries
        if (entry.isIntersecting && !isIntersecting) {
          const delay = options.priority === 'critical' ? 0 : 
                       options.priority === 'high' ? 50 :
                       options.priority === 'medium' ? 100 : 200

          setTimeout(() => {
            setIsIntersecting(true)
            setHasLoaded(true)
            observer.disconnect()
          }, delay)
        }
      },
      {
        rootMargin: options.rootMargin || '50px',
        threshold: options.threshold || 0.1,
      }
    )

    observer.observe(currentRef)

    return () => observer.disconnect()
  }, [isIntersecting, options.rootMargin, options.threshold, options.priority])

  return {
    elementRef,
    isIntersecting,
    hasLoaded,
  }
}

