// Hook for memoizing expensive calculations
export function useMemoizedCalculation<T>(
  calculation: () => T,
  dependencies: React.DependencyList,
  options?: {
    maxAge?: number // Cache duration in milliseconds
    key?: string    // Cache key for debugging
  }
): T {
  const cacheRef = useRef<{
    value: T
    timestamp: number
    deps: React.DependencyList
  } | null>(null)

  return useMemo(() => {
    const now = Date.now()
    const cache = cacheRef.current

    // Check if we have a valid cache
    if (
      cache &&
      (!options?.maxAge || now - cache.timestamp < options.maxAge) &&
      dependencies.length === cache.deps.length &&
      dependencies.every((dep, index) => dep === cache.deps[index])
    ) {
      if (options?.key) {
        console.log(`Cache hit for ${options.key}`)
      }
      return cache.value
    }

    // Calculate new value
    if (options?.key) {
      console.log(`Cache miss for ${options.key}, recalculating...`)
    }

    const startTime = performance.now()
    const value = calculation()
    const endTime = performance.now()

    if (options?.key) {
      console.log(`Calculation ${options.key} took ${(endTime - startTime).toFixed(2)}ms`)
    }

    // Update cache
    cacheRef.current = {
      value,
      timestamp: now,
      deps: [...dependencies],
    }

    return value
  }, [calculation, dependencies, options?.key, options?.maxAge])
}

// Hook for memoizing callback functions
export function useMemoizedCallback<T extends (...args: any[]) => any>(
  callback: T,
  dependencies: React.DependencyList
): T {
  return useCallback(callback, dependencies)
}

// Hook for stable object references
export function useStableObject<T extends object>(obj: T): T {
  const stableRef = useRef<T>(obj)

  // Only update if the object has actually changed
  const hasChanged = useMemo(() => {
    const current = stableRef.current

    // Shallow comparison
    if (Object.keys(current).length !== Object.keys(obj).length) {
      return true
    }

    for (const key in obj) {
      if (current[key] !== obj[key]) {
        return true
      }
    }

    return false
  }, [obj])

  if (hasChanged) {
    stableRef.current = obj
  }

  return stableRef.current
}

// Performance monitoring for memoized components
export function withPerformanceMonitoring<T extends object>(
  Component: React.ComponentType<T>,
  componentName?: string
) {
  const PerformanceMonitoredComponent = React.forwardRef<any, T>((props, ref) => {
    const renderCountRef = useRef(0)
    const lastRenderTime = useRef(0)

    useEffect(() => {
      renderCountRef.current += 1
      const now = performance.now()

      if (lastRenderTime.current > 0) {
        const timeSinceLastRender = now - lastRenderTime.current
        console.log(
          `${componentName || Component.displayName || Component.name} render #${renderCountRef.current} ` +
          `(${timeSinceLastRender.toFixed(2)}ms since last render)`
        )
      }

      lastRenderTime.current = now
    })

    const renderStart = performance.now()

    useEffect(() => {
      const renderEnd = performance.now()
      const renderTime = renderEnd - renderStart

      if (renderTime > 16) { // Flag slow renders (>16ms = <60fps)
        console.warn(
          `Slow render detected: ${componentName || Component.displayName || Component.name} ` +
          `took ${renderTime.toFixed(2)}ms to render`
        )
      }
    })

    return <Component {...props} ref={ref} />
  })

  PerformanceMonitoredComponent.displayName = `WithPerformanceMonitoring(${componentName || Component.displayName || Component.name})`

  return PerformanceMonitoredComponent
}

// Render optimization utilities
export class RenderOptimizer {
  private static renderQueue: Array<() => void> = []
  private static isProcessing = false

  // Batch multiple state updates
  static batchUpdates(updates: Array<() => void>) {
    this.renderQueue.push(...updates)

    if (!this.isProcessing) {
      this.isProcessing = true

      // Use scheduler if available, otherwise fallback to setTimeout
      if (typeof window !== 'undefined' && 'scheduler' in window) {
        (window as any).scheduler.postTask(() => {
          this.processQueue()
        }, { priority: 'user-blocking' })
      } else {
        setTimeout(() => {
          this.processQueue()
        }, 0)
      }
    }
  }

  private static processQueue() {
    const updates = [...this.renderQueue]
    this.renderQueue.length = 0

    // Execute all updates in a single batch
    React.unstable_batchedUpdates(() => {
      updates.forEach(update => update())
    })

    this.isProcessing = false

    // Process any new updates that were added during processing
    if (this.renderQueue.length > 0) {
      this.batchUpdates([])
    }
  }

  // Debounce rapid updates
  static debounceUpdate(
    updateFn: () => void,
    delay: number = 100,
    key: string = 'default'
  ) {
    const timers = this.debounceTimers || (this.debounceTimers = new Map())

    if (timers.has(key)) {
      clearTimeout(timers.get(key))
    }

    timers.set(key, setTimeout(() => {
      updateFn()
      timers.delete(key)
    }, delay))
  }

  private static debounceTimers: Map<string, NodeJS.Timeout>

  // Throttle high-frequency updates
  static throttleUpdate(
    updateFn: () => void,
    delay: number = 16, // ~60fps
    key: string = 'default'
  ) {
    const lastExecution = this.throttleTimestamps || (this.throttleTimestamps = new Map())
    const now = Date.now()
    const lastTime = lastExecution.get(key) || 0

    if (now - lastTime >= delay) {
      updateFn()
      lastExecution.set(key, now)
    }
  }

  private static throttleTimestamps: Map<string, number>
}

// Hook for optimized re-renders
export function useOptimizedRender() {
  const [, forceRender] = React.useReducer(x => x + 1, 0)

  const scheduleUpdate = useCallback((priority: 'low' | 'normal' | 'high' = 'normal') => {
    switch (priority) {
      case 'high':
        // Immediate update
        forceRender()
        break
      case 'normal':
        // Batched update
        RenderOptimizer.batchUpdates([forceRender])
        break
      case 'low':
        // Debounced update
        RenderOptimizer.debounceUpdate(forceRender, 100)
        break
    }
  }, [])

  return { scheduleUpdate }
}

// Component for preventing unnecessary re-renders of children
export const RenderBarrier = memo(({ children }: { children: React.ReactNode }) => {
  return <>{children}</>
})

RenderBarrier.displayName = 'RenderBarrier'

// Hook for tracking render performance
export function useRenderPerformance(componentName: string) {
  const renderCount = useRef(0)
  const renderTimes = useRef<number[]>([])
  const startTime = useRef(0)

  // Mark render start
  startTime.current = performance.now()

  useEffect(() => {
    const endTime = performance.now()
    const renderTime = endTime - startTime.current

    renderCount.current += 1
    renderTimes.current.push(renderTime)

    // Keep only last 10 render times
    if (renderTimes.current.length > 10) {
      renderTimes.current.shift()
    }

    const avgRenderTime = renderTimes.current.reduce((a, b) => a + b, 0) / renderTimes.current.length

    if (renderTime > 16) {
      console.warn(
        `${componentName} slow render: ${renderTime.toFixed(2)}ms ` +
        `(avg: ${avgRenderTime.toFixed(2)}ms, count: ${renderCount.current})`
      )
    }

    // Send performance data to analytics
    if (typeof window !== 'undefined' && 'gtag' in window) {
      (window as any).gtag('event', 'component_render_performance', {
        component_name: componentName,
        render_time: Math.round(renderTime),
        render_count: renderCount.current,
        avg_render_time: Math.round(avgRenderTime),
      })
    }
  })

  return {
    renderCount: renderCount.current,
    averageRenderTime: renderTimes.current.length > 0 
      ? renderTimes.current.reduce((a, b) => a + b, 0) / renderTimes.current.length 
      : 0,
  }
}

import React, { useMemo } from 'react'
import { cn } from '@/lib/utils'
import { Skeleton } from '@/components/ui/skeleton'
import { Card, CardContent, CardHeader } from '@/components/ui/card'
import { useIntersectionObserver, getViewportBasedPriority } from './intersection-observer'

interface ViewportSkeletonProps {
  children: React.ReactNode
  className?: string
  variant?: 'card' | 'chart' | 'table' | 'metric' | 'list' | 'grid'
  priority?: 'critical' | 'high' | 'medium' | 'low' | 'auto'
  height?: number
  width?: number
  rows?: number
  columns?: number
  animated?: boolean
  showShimmer?: boolean
  adaptiveComplexity?: boolean
  onVisibilityChange?: (visible: boolean) => void
}

interface SkeletonConfig {
  baseDelay: number
  complexityMultiplier: number
  animationDuration: string
  shimmerIntensity: number
}

// Priority-based skeleton configurations
const SKELETON_CONFIGS: Record<string, SkeletonConfig> = {
  critical: {
    baseDelay: 0,
    complexityMultiplier: 1,
    animationDuration: '1s',
    shimmerIntensity: 0.8,
  },
  high: {
    baseDelay: 50,
    complexityMultiplier: 0.8,
    animationDuration: '1.2s',
    shimmerIntensity: 0.6,
  },
  medium: {
    baseDelay: 100,
    complexityMultiplier: 0.6,
    animationDuration: '1.5s',
    shimmerIntensity: 0.4,
  },
  low: {
    baseDelay: 200,
    complexityMultiplier: 0.4,
    animationDuration: '2s',
    shimmerIntensity: 0.2,
  },
}

export function ViewportSkeleton({
  children,
  className,
  variant = 'card',
  priority = 'auto',
  height = 200,
  width,
  rows = 3,
  columns = 3,
  animated = true,
  showShimmer = true,
  adaptiveComplexity = true,
  onVisibilityChange,
}: ViewportSkeletonProps) {
  const { ref, inView } = useIntersectionObserver({
    threshold: 0.1,
    rootMargin: '50px',
    triggerOnce: true,
  })

  // Determine priority automatically based on viewport position
  const effectivePriority = useMemo(() => {
    if (priority !== 'auto') return priority

    // Get priority based on viewport position when element is available
    return 'medium' // Default fallback
  }, [priority])

  const config = SKELETON_CONFIGS[effectivePriority]

  // Handle visibility changes
  React.useEffect(() => {
    onVisibilityChange?.(inView)
  }, [inView, onVisibilityChange])

  // Render appropriate skeleton based on variant
  const skeletonContent = useMemo(() => {
    const baseProps = {
      animated,
      showShimmer,
      config,
      adaptiveComplexity,
    }

    switch (variant) {
      case 'card':
        return <CardSkeleton {...baseProps} height={height} />
      case 'chart':
        return <ChartSkeleton {...baseProps} height={height} />
      case 'table':
        return <TableSkeleton {...baseProps} rows={rows} columns={columns} />
      case 'metric':
        return <MetricSkeleton {...baseProps} />
      case 'list':
        return <ListSkeleton {...baseProps} rows={rows} />
      case 'grid':
        return <GridSkeleton {...baseProps} rows={rows} columns={columns} />
      default:
        return <CardSkeleton {...baseProps} height={height} />
    }
  }, [variant, height, rows, columns, animated, showShimmer, config, adaptiveComplexity])

  // Render content if in view
  if (inView) {
    return <div className={className}>{children}</div>
  }

  return (
    <div
      ref={ref}
      className={cn(
        'viewport-skeleton',
        `viewport-skeleton-${variant}`,
        `viewport-skeleton-priority-${effectivePriority}`,
        className
      )}
      style={{
        minHeight: height,
        width: width ? `${width}px` : undefined,
      }}
      role="status"
      aria-label={`Loading ${variant} content`}
    >
      {skeletonContent}
    </div>
  )
}

