'use client'

import * as React from 'react'
import { useDashboardTheme } from './theme-provider'
import { DashboardTheme } from '@/types/dashboard'

interface ContrastValidatorProps {
  className?: string
  showDetails?: boolean
  autoValidate?: boolean
  onValidationComplete?: (results: ValidationResult[]) => void
}

interface ValidationResult {
  id: string
  colorPair: {
    foreground: string
    background: string
    foregroundName: string
    backgroundName: string
  }
  contrastRatio: number
  wcagLevel: 'AAA' | 'AA' | 'FAIL'
  isValid: boolean
  recommendation?: string
}

interface ContrastTestPair {
  id: string
  foreground: string
  background: string
  foregroundName: string
  backgroundName: string
  requiredRatio: number
  context: 'text' | 'ui' | 'graphical'
}

// WCAG 2.1 contrast requirements
const WCAG_REQUIREMENTS = {
  AA_NORMAL: 4.5,
  AA_LARGE: 3.0,
  AAA_NORMAL: 7.0,
  AAA_LARGE: 4.5,
  UI_COMPONENTS: 3.0,
  GRAPHICAL: 3.0
}

function getWCAGLevel(ratio: number, isLargeText: boolean = false): 'AAA' | 'AA' | 'FAIL' {
  if (isLargeText) {
    if (ratio >= WCAG_REQUIREMENTS.AAA_LARGE) return 'AAA'
    if (ratio >= WCAG_REQUIREMENTS.AA_LARGE) return 'AA'
  } else {
    if (ratio >= WCAG_REQUIREMENTS.AAA_NORMAL) return 'AAA'
    if (ratio >= WCAG_REQUIREMENTS.AA_NORMAL) return 'AA'
  }
  return 'FAIL'
}

function generateRecommendation(result: ValidationResult): string {
  const { contrastRatio, wcagLevel } = result

  if (wcagLevel === 'FAIL') {
    const needed = WCAG_REQUIREMENTS.AA_NORMAL
    const improvement = ((needed / contrastRatio - 1) * 100).toFixed(0)
    return `Increase contrast by approximately ${improvement}% to meet WCAG AA standards`
  }

  if (wcagLevel === 'AA') {
    const needed = WCAG_REQUIREMENTS.AAA_NORMAL
    const improvement = ((needed / contrastRatio - 1) * 100).toFixed(0)
    return `Consider increasing contrast by ${improvement}% to achieve WCAG AAA level`
  }

  return 'Excellent contrast - meets WCAG AAA standards'
}

export function ContrastValidator({
  className = '',
  showDetails = true,
  autoValidate = true,
  onValidationComplete
}: ContrastValidatorProps) {
  const { dashboardTheme, getContrastRatio } = useDashboardTheme()
  const [validationResults, setValidationResults] = React.useState<ValidationResult[]>([])
  const [isValidating, setIsValidating] = React.useState(false)

  // Generate test pairs based on current theme
  const generateTestPairs = React.useCallback((): ContrastTestPair[] => {
    const { colors } = dashboardTheme
    const pairs: ContrastTestPair[] = []

    // Text colors against backgrounds
    Object.entries(colors.text).forEach(([textKey, textColor]) => {
      pairs.push({
        id: `text-${textKey}-bg`,
        foreground: textColor,
        background: colors.background,
        foregroundName: `Text ${textKey}`,
        backgroundName: 'Background',
        requiredRatio: WCAG_REQUIREMENTS.AA_NORMAL,
        context: 'text'
      })

      pairs.push({
        id: `text-${textKey}-surface`,
        foreground: textColor,
        background: colors.surface,
        foregroundName: `Text ${textKey}`,
        backgroundName: 'Surface',
        requiredRatio: WCAG_REQUIREMENTS.AA_NORMAL,
        context: 'text'
      })
    })

    // Status colors against backgrounds
    Object.entries(colors.status).forEach(([statusKey, statusColor]) => {
      pairs.push({
        id: `status-${statusKey}-bg`,
        foreground: statusColor,
        background: colors.background,
        foregroundName: `Status ${statusKey}`,
        backgroundName: 'Background',
        requiredRatio: WCAG_REQUIREMENTS.UI_COMPONENTS,
        context: 'ui'
      })
    })

    // Primary colors against backgrounds
    pairs.push({
      id: 'primary-bg',
      foreground: colors.primary,
      background: colors.background,
      foregroundName: 'Primary',
      backgroundName: 'Background',
      requiredRatio: WCAG_REQUIREMENTS.UI_COMPONENTS,
      context: 'ui'
    })

    pairs.push({
      id: 'secondary-bg',
      foreground: colors.secondary,
      background: colors.background,
      foregroundName: 'Secondary',
      backgroundName: 'Background',
      requiredRatio: WCAG_REQUIREMENTS.UI_COMPONENTS,
      context: 'ui'
    })

    pairs.push({
      id: 'accent-bg',
      foreground: colors.accent,
      background: colors.background,
      foregroundName: 'Accent',
      backgroundName: 'Background',
      requiredRatio: WCAG_REQUIREMENTS.UI_COMPONENTS,
      context: 'ui'
    })

    return pairs
  }, [dashboardTheme])

  // Validate contrast ratios
  const validateContrast = React.useCallback(async () => {
    setIsValidating(true)
    const testPairs = generateTestPairs()
    const results: ValidationResult[] = []

    for (const pair of testPairs) {
      const ratio = getContrastRatio(pair.foreground, pair.background)
      const wcagLevel = getWCAGLevel(ratio, false) // Assuming normal text size
      const isValid = ratio >= pair.requiredRatio

      const result: ValidationResult = {
        id: pair.id,
        colorPair: {
          foreground: pair.foreground,
          background: pair.background,
          foregroundName: pair.foregroundName,
          backgroundName: pair.backgroundName
        },
        contrastRatio: ratio,
        wcagLevel,
        isValid,
        recommendation: generateRecommendation({
          id: pair.id,
          colorPair: {
            foreground: pair.foreground,
            background: pair.background,
            foregroundName: pair.foregroundName,
            backgroundName: pair.backgroundName
          },
          contrastRatio: ratio,
          wcagLevel,
          isValid
        })
      }

      results.push(result)
    }

    setValidationResults(results)
    setIsValidating(false)

    if (onValidationComplete) {
      onValidationComplete(results)
    }
  }, [generateTestPairs, getContrastRatio, onValidationComplete])

  // Auto-validate when theme changes
  React.useEffect(() => {
    if (autoValidate) {
      validateContrast()
    }
  }, [autoValidate, validateContrast])

  const failedTests = validationResults.filter(result => !result.isValid)
  const passedTests = validationResults.filter(result => result.isValid)
  const aaaTests = validationResults.filter(result => result.wcagLevel === 'AAA')

  return (
    <div className={`space-y-6 ${className}`}>
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100">
            Contrast Validation
          </h3>
          <p className="text-sm text-gray-600 dark:text-gray-400">
            WCAG 2.1 accessibility compliance check
          </p>
        </div>
        <button
          onClick={validateContrast}
          disabled={isValidating}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isValidating ? 'Validating...' : 'Validate'}
        </button>
      </div>

      {/* Summary */}
      {validationResults.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-green-50 dark:bg-green-900/20 p-4 rounded-lg">
            <div className="text-2xl font-bold text-green-600 dark:text-green-400">
              {passedTests.length}
            </div>
            <div className="text-sm text-green-800 dark:text-green-200">
              Passed Tests
            </div>
          </div>

          <div className="bg-red-50 dark:bg-red-900/20 p-4 rounded-lg">
            <div className="text-2xl font-bold text-red-600 dark:text-red-400">
              {failedTests.length}
            </div>
            <div className="text-sm text-red-800 dark:text-red-200">
              Failed Tests
            </div>
          </div>

          <div className="bg-purple-50 dark:bg-purple-900/20 p-4 rounded-lg">
            <div className="text-2xl font-bold text-purple-600 dark:text-purple-400">
              {aaaTests.length}
            </div>
            <div className="text-sm text-purple-800 dark:text-purple-200">
              AAA Level
            </div>
          </div>
        </div>
      )}

      {/* Detailed Results */}
      {showDetails && validationResults.length > 0 && (
        <div className="space-y-4">
          <h4 className="text-md font-semibold text-gray-900 dark:text-gray-100">
            Detailed Results
          </h4>

          {/* Failed Tests First */}
          {failedTests.length > 0 && (
            <div className="space-y-3">
              <h5 className="text-sm font-medium text-red-600 dark:text-red-400">
                Failed Tests ({failedTests.length})
              </h5>
              {failedTests.map((result) => (
                <div
                  key={result.id}
                  className="border border-red-200 dark:border-red-800 rounded-lg p-4 bg-red-50 dark:bg-red-900/10"
                >
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center space-x-3">
                      <div className="flex items-center space-x-2">
                        <div
                          className="w-4 h-4 rounded border"
                          style={{ backgroundColor: result.colorPair.foreground }}
                        />
                        <span className="text-sm font-medium">
                          {result.colorPair.foregroundName}
                        </span>
                        <span className="text-xs text-gray-500">on</span>
                        <div
                          className="w-4 h-4 rounded border"
                          style={{ backgroundColor: result.colorPair.background }}
                        />
                        <span className="text-sm font-medium">
                          {result.colorPair.backgroundName}
                        </span>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="text-sm font-mono text-red-600 dark:text-red-400">
                        {result.contrastRatio.toFixed(2)}:1
                      </div>
                      <div className="text-xs text-red-500">
                        {result.wcagLevel}
                      </div>
                    </div>
                  </div>
                  <div className="text-xs text-red-700 dark:text-red-300">
                    {result.recommendation}
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Passed Tests */}
          {passedTests.length > 0 && (
            <div className="space-y-3">
              <h5 className="text-sm font-medium text-green-600 dark:text-green-400">
                Passed Tests ({passedTests.length})
              </h5>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                {passedTests.map((result) => (
                  <div
                    key={result.id}
                    className="border border-green-200 dark:border-green-800 rounded-lg p-3 bg-green-50 dark:bg-green-900/10"
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <div
                          className="w-3 h-3 rounded border"
                          style={{ backgroundColor: result.colorPair.foreground }}
                        />
                        <span className="text-xs">
                          {result.colorPair.foregroundName} / {result.colorPair.backgroundName}
                        </span>
                      </div>
                      <div className="text-right">
                        <div className="text-xs font-mono text-green-600 dark:text-green-400">
                          {result.contrastRatio.toFixed(2)}:1
                        </div>
                        <div className="text-xs text-green-500">
                          {result.wcagLevel}
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {/* WCAG Guidelines */}
      <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
        <h4 className="text-sm font-semibold mb-3 text-gray-900 dark:text-gray-100">
          WCAG 2.1 Contrast Requirements
        </h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-xs text-gray-600 dark:text-gray-400">
          <div>
            <div className="font-medium mb-1">Normal Text</div>
            <div>AA: 4.5:1 minimum</div>
            <div>AAA: 7:1 minimum</div>
          </div>
          <div>
            <div className="font-medium mb-1">Large Text (18pt+)</div>
            <div>AA: 3:1 minimum</div>
            <div>AAA: 4.5:1 minimum</div>
          </div>
          <div>
            <div className="font-medium mb-1">UI Components</div>
            <div>AA: 3:1 minimum</div>
          </div>
          <div>
            <div className="font-medium mb-1">Graphical Objects</div>
            <div>AA: 3:1 minimum</div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default ContrastValidator

'use client'

import * as React from 'react'
import { useDashboardTheme } from './theme-provider'
import { ColorPalette } from './color-palette'
import { TypographyScale } from './typography-scale'
import { ContrastValidator } from './contrast-validator'
import { DashboardTheme } from '@/types/dashboard'

interface ThemeCustomizerProps {
  className?: string
  defaultTab?: 'colors' | 'typography' | 'validation'
  onThemeChange?: (theme: Partial<DashboardTheme>) => void
  onExport?: (theme: DashboardTheme) => void
  onImport?: (theme: Partial<DashboardTheme>) => void
}

interface TabProps {
  id: string
  label: string
  icon: React.ReactNode
  content: React.ReactNode
}

export function ThemeCustomizer({
  className = '',
  defaultTab = 'colors',
  onThemeChange,
  onExport,
  onImport
}: ThemeCustomizerProps) {
  const { theme, setTheme, dashboardTheme, setDashboardTheme } = useDashboardTheme()
  const [activeTab, setActiveTab] = React.useState(defaultTab)
  const [validationResults, setValidationResults] = React.useState<any[]>([])
  const fileInputRef = React.useRef<HTMLInputElement>(null)

  const handleThemeChange = React.useCallback((newTheme: Partial<DashboardTheme>) => {
    setDashboardTheme(newTheme)
    if (onThemeChange) {
      onThemeChange(newTheme)
    }
  }, [setDashboardTheme, onThemeChange])

  const handleExportTheme = () => {
    const themeData = {
      ...dashboardTheme,
      metadata: {
        name: 'Custom Dashboard Theme',
        version: '1.0.0',
        createdAt: new Date().toISOString(),
        description: 'Exported from Dashboard Theme Customizer'
      }
    }

    const blob = new Blob([JSON.stringify(themeData, null, 2)], {
      type: 'application/json'
    })

    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = 'dashboard-theme.json'
    document.body.appendChild(a)
    a.click()
    document.body.removeChild(a)
    URL.revokeObjectURL(url)

    if (onExport) {
      onExport(dashboardTheme)
    }
  }

  const handleImportTheme = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]
    if (!file) return

    const reader = new FileReader()
    reader.onload = (e) => {
      try {
        const importedTheme = JSON.parse(e.target?.result as string)

        // Validate that it's a valid theme object
        if (importedTheme.colors && importedTheme.typography) {
          setDashboardTheme(importedTheme)
          if (onImport) {
            onImport(importedTheme)
          }
        } else {
          alert('Invalid theme file format')
        }
      } catch (error) {
        alert('Error parsing theme file')
      }
    }
    reader.readAsText(file)
  }

  const resetToDefaults = () => {
    if (confirm('Are you sure you want to reset all theme customizations? This cannot be undone.')) {
      // Reset to default theme by reloading
      window.location.reload()
    }
  }

  const tabs: TabProps[] = [
    {
      id: 'colors',
      label: 'Colors',
      icon: (
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 21a4 4 0 01-4-4V5a2 2 0 012-2h4a2 2 0 012 2v12a4 4 0 01-4 4zM21 5a2 2 0 00-2-2h-4a2 2 0 00-2 2v12a4 4 0 004 4h4a2 2 0 002-2V5z" />
        </svg>
      ),
      content: (
        <ColorPalette
          interactive={true}
          onColorChange={(colorPath, newColor) => {
            console.log(`Color changed: ${colorPath} = ${newColor}`)
          }}
        />
      )
    },
    {
      id: 'typography',
      label: 'Typography',
      icon: (
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
        </svg>
      ),
      content: (
        <TypographyScale
          interactive={true}
          onTypographyChange={(key, value) => {
            console.log(`Typography changed: ${key} = ${value}`)
          }}
        />
      )
    },
    {
      id: 'validation',
      label: 'Validation',
      icon: (
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      ),
      content: (
        <ContrastValidator
          onValidationComplete={setValidationResults}
        />
      )
    }
  ]

  const failedValidations = validationResults.filter(result => !result.isValid).length
  const hasValidationIssues = failedValidations > 0

  return (
    <div className={`bg-white dark:bg-gray-900 rounded-lg shadow-lg ${className}`}>
      {/* Header */}
      <div className="border-b border-gray-200 dark:border-gray-700 p-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-xl font-semibold text-gray-900 dark:text-gray-100">
              Theme Customizer
            </h2>
            <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
              Customize colors, typography, and validate accessibility compliance
            </p>
          </div>

          {/* Theme Mode Toggle */}
          <div className="flex items-center space-x-2">
            <label className="text-sm text-gray-700 dark:text-gray-300">
              Theme:
            </label>
            <select
              value={theme}
              onChange={(e) => setTheme(e.target.value as 'light' | 'dark' | 'system')}
              className="px-3 py-1 border border-gray-300 dark:border-gray-600 rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100"
            >
              <option value="light">Light</option>
              <option value="dark">Dark</option>
              <option value="system">System</option>
            </select>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex items-center space-x-3 mt-4">
          <button
            onClick={handleExportTheme}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-sm"
          >
            Export Theme
          </button>

          <button
            onClick={() => fileInputRef.current?.click()}
            className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 text-sm"
          >
            Import Theme
          </button>

          <button
            onClick={resetToDefaults}
            className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 text-sm"
          >
            Reset to Defaults
          </button>

          <input
            ref={fileInputRef}
            type="file"
            accept=".json"
            onChange={handleImportTheme}
            className="hidden"
          />
        </div>
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200 dark:border-gray-700">
        <nav className="flex space-x-8 px-6" aria-label="Tabs">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`
                flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm
                ${activeTab === tab.id
                  ? 'border-blue-500 text-blue-600 dark:text-blue-400'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 dark:text-gray-400 dark:hover:text-gray-300'
                }
              `}
            >
              {tab.icon}
              <span>{tab.label}</span>
              {tab.id === 'validation' && hasValidationIssues && (
                <span className="bg-red-100 text-red-800 text-xs px-2 py-1 rounded-full">
                  {failedValidations}
                </span>
              )}
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      <div className="p-6">
        {tabs.find(tab => tab.id === activeTab)?.content}
      </div>

      {/* Footer */}
      <div className="border-t border-gray-200 dark:border-gray-700 p-4 bg-gray-50 dark:bg-gray-800 rounded-b-lg">
        <div className="flex items-center justify-between text-sm text-gray-600 dark:text-gray-400">
          <div className="flex items-center space-x-4">
            <span>Current Theme: {theme}</span>
            {validationResults.length > 0 && (
              <span>
                Accessibility: {validationResults.length - failedValidations}/{validationResults.length} passed
              </span>
            )}
          </div>
          <div className="text-xs">
            Changes are applied automatically
          </div>
        </div>
      </div>
    </div>
  )
}

export default ThemeCustomizer
